00002 //
00003 // Purpose: Collider dangling from the player's head
00004 //
00005 //=============================================================================
00006 
00007 using UnityEngine;
00008 using System.Collections;
00009 
00010 namespace Valve.VR.InteractionSystem
00011 {
00012     //-------------------------------------------------------------------------
00013     [RequireComponent( typeof( CapsuleCollider ) )]
00014     public class BodyCollider : MonoBehaviour
00015     {
00016         public Transform head;
00017 
00018         private CapsuleCollider capsuleCollider;
00019 
00020         //-------------------------------------------------
00021         void Awake()
00022         {
00023             capsuleCollider = GetComponent<CapsuleCollider>();
00024         }
00025 
00026 
00027         //-------------------------------------------------
00028         void FixedUpdate()
00029         {
00030             float distanceFromFloor = Vector3.Dot( head.localPosition, Vector3.up );
00031             capsuleCollider.height = Mathf.Max( capsuleCollider.radius, distanceFromFloor );
00032             transform.localPosition = head.localPosition - 0.5f * distanceFromFloor * Vector3.up;
00033         }
00034     }
00035 }
