00010 using UnityEngine;
00011 using UnityEditor;
00012 
00013 namespace Leap.Unity {
00014 
00015   public static class CorePreferences {
00016 
00017     private const string ALLOW_CLEAR_TRANSFORM_HOTKEY_KEY = "LeapMotion_AllowClearTransformHotkey";
00018     private const string ALLOW_GROUP_OBJECTS_HOTKEY_KEY = "LeapMotion_AllowGroupObjectsHotkey";
00019 
00020     public static bool allowClearTransformHotkey {
00021       get {
00022         return EditorPrefs.GetBool(ALLOW_CLEAR_TRANSFORM_HOTKEY_KEY, defaultValue: false);
00023       }
00024       set {
00025         EditorPrefs.SetBool(ALLOW_CLEAR_TRANSFORM_HOTKEY_KEY, value);
00026       }
00027     }
00028 
00029     public static bool allowGroupObjectsHotkey {
00030       get {
00031         return EditorPrefs.GetBool(ALLOW_GROUP_OBJECTS_HOTKEY_KEY, defaultValue: false);
00032       }
00033       set {
00034         EditorPrefs.SetBool(ALLOW_GROUP_OBJECTS_HOTKEY_KEY, value);
00035       }
00036     }
00037 
00038     [LeapPreferences("Core", 0)]
00039     private static void drawCorePreferences() {
00040       drawPreferencesBool(ALLOW_CLEAR_TRANSFORM_HOTKEY_KEY, "Clear Transforms Hotkey", "When you press Ctrl+E, clear out the local position, rotation, and scale of the selected transforms.");
00041       drawPreferencesBool(ALLOW_GROUP_OBJECTS_HOTKEY_KEY, "Group Transforms Hotkey", "When you press Ctrl+G, group all selected objects underneath a single new object named Group.");
00042     }
00043 
00044     private static void drawPreferencesBool(string key, string label, string tooltip) {
00045       GUIContent content = new GUIContent(label, tooltip);
00046 
00047       bool value = EditorPrefs.GetBool(key, defaultValue: false);
00048       var newValue = EditorGUILayout.Toggle(content, value);
00049       if (newValue != value) {
00050         EditorPrefs.SetBool(key, newValue);
00051       }
00052     }
00053   }
00054 }
