00010 using System;
00011 using System.Collections;
00012 using System.Collections.Generic;
00013 using UnityEngine;
00014 
00015 #if UNITY_2017_2_OR_NEWER
00016 using UnityEngine.XR;
00017 #else
00018 using UnityEngine.VR;
00019 #endif
00020 
00021 namespace Leap.Unity.Interaction {
00022   
00028   public class DefaultXRNodeTrackingProvider : MonoBehaviour,
00029                                                IXRControllerTrackingProvider {
00030 
00031     private bool _isTrackingController = false;
00032     public bool isTracked { get { return _isTrackingController; } }
00033 
00034     private bool _isXRNodeSet = false;
00035     #if UNITY_2017_2_OR_NEWER
00036     private XRNode _backingXRNode;
00037     public XRNode xrNode {
00038       get { return _backingXRNode; }
00039       set { _backingXRNode = value; _isXRNodeSet = true; }
00040     }
00041     #else
00042     private VRNode _backingXRNode;
00043     public VRNode xrNode {
00044       get { return _backingXRNode; }
00045       set { _backingXRNode = value; _isXRNodeSet = true; }
00046     }
00047     #endif
00048 
00049     public event Action<Vector3, Quaternion> OnTrackingDataUpdate = (position, rotation) => { };
00050 
00051     void FixedUpdate() {
00052       updateTrackingData();
00053     }
00054 
00055     void updateTrackingData() {
00056       if (_isXRNodeSet) {
00057 
00058         var position = InputTracking.GetLocalPosition(xrNode);
00059         var rotation = InputTracking.GetLocalRotation(xrNode);
00060 
00061         // Unfortunately, the only alternative to checking the controller's position and
00062         // rotation for whether or not it is tracked is to request an allocated string
00063         // array of all currently-connected joysticks, which would allocate garbage
00064         // every frame, so it's unusable.
00065         _isTrackingController = position != Vector3.zero && rotation != Quaternion.identity;
00066 
00067         Transform rigTransform = Camera.main.transform.parent;
00068         if (rigTransform != null) {
00069           position = rigTransform.TransformPoint(position);
00070           rotation = rigTransform.TransformRotation(rotation);
00071         }
00072 
00073         OnTrackingDataUpdate(position, rotation);
00074       }
00075     }
00076 
00077   }
00078 
00079 }
