00003 // Purpose: Destroys this object when its particle system dies
00004 //
00005 //=============================================================================
00006 
00007 using UnityEngine;
00008 using System.Collections;
00009 
00010 namespace Valve.VR.InteractionSystem
00011 {
00012     //-------------------------------------------------------------------------
00013     [RequireComponent( typeof( ParticleSystem ) )]
00014     public class DestroyOnParticleSystemDeath : MonoBehaviour
00015     {
00016         private ParticleSystem particles;
00017 
00018         //-------------------------------------------------
00019         void Awake()
00020         {
00021             particles = GetComponent<ParticleSystem>();
00022 
00023             InvokeRepeating( "CheckParticleSystem", 0.1f, 0.1f );
00024         }
00025 
00026 
00027         //-------------------------------------------------
00028         private void CheckParticleSystem()
00029         {
00030             if ( !particles.IsAlive() )
00031             {
00032                 Destroy( this.gameObject );
00033             }
00034         }
00035     }
00036 }
