00042             SerializedProperty itemImage = itemData.FindPropertyRelative("m_Image");
00043 
00044             RectOffset offset = new RectOffset(0, 0, -1, -3);
00045             rect = offset.Add(rect);
00046             rect.height = EditorGUIUtility.singleLineHeight;
00047 
00048             EditorGUI.PropertyField(rect, itemText, GUIContent.none);
00049             rect.y += EditorGUIUtility.singleLineHeight;
00050             EditorGUI.PropertyField(rect, itemImage, GUIContent.none);
00051         }
