00019       public static float InOut(float t) {
00020         t *= 2F;
00021         if (t < 1F) return 0.5F * t * t;
00022         t -= 1F;
00023         return (-0.5F) * (t * (t - 2F) - 1F);
00024       }
00025 
00026     }
