00028     public static class Cubic {
00029 
00032       public static float InOut(float t) {
00033         t *= 2F;
00034         if (t < 1F) return 0.5F * t * t * t;
00035         t -= 2F;
00036         return 0.5F * (t * t * t + 2F);
00037       }
00038 
00039     }
00040 
00041     public static class Quartic {
00042 
00045       public static float InOut(float t) {
00046         t *= 2F;
00047         if (t < 1F) return 0.5F * t * t * t * t;
00048         t -= 2F;
00049         return -0.5F * (t * t * t * t - 2F);
00050       }
00051 
00052     }
