00010 using System.IO;
00011 using System.Linq;
00012 using UnityEngine;
00013 #if UNITY_EDITOR
00014 using UnityEditor;
00015 #endif
00016 
00017 namespace Leap.Unity {
00018 
00019 #if UNITY_EDITOR
00020   public static class EditorResources {
00021 
00026     public static T[] FindAllAssetsOfType<T>() where T : Object {
00027       return AssetDatabase.FindAssets("t:" + typeof(T).Name).
00028                            Select(guid => AssetDatabase.GUIDToAssetPath(guid)).
00029                            Select(path => AssetDatabase.LoadAssetAtPath<T>(path)).
00030                            ToArray();
00031     }
00032 
00038     public static T Load<T>(string name) where T : Object {
00039       foreach (var rootDir in Directory.GetDirectories("Assets", "EditorResources", SearchOption.AllDirectories)) {
00040         string fullPath = Path.Combine(rootDir, name + ".dummy");
00041         string fullDir = Path.GetDirectoryName(fullPath);
00042         string fileName = Path.GetFileNameWithoutExtension(fullPath);
00043 
00044         if (!Directory.Exists(fullDir)) {
00045           continue;
00046         }
00047 
00048         foreach (var filename in Directory.GetFiles(fullDir, fileName + ".*")) {
00049           if (!string.IsNullOrEmpty(AssetDatabase.AssetPathToGUID(filename))) {
00050             return AssetDatabase.LoadAssetAtPath<T>(filename);
00051           }
00052         }
00053       }
00054       return null;
00055     }
00056   }
00057 #endif
00058 }
