00010 using Leap.Unity.Query;
00011 using System;
00012 using System.Collections.Generic;
00013 using System.Reflection;
00014 using UnityEditor;
00015 using UnityEngine;
00016 using UnityEngine.SceneManagement;
00017 
00018 namespace Leap.Unity {
00019 
00020   using UnityObject = UnityEngine.Object;
00021 
00022   public static class EditorUtils {
00023 
00024     private class SceneReference<T> where T : UnityObject {
00025       SerializedObject objectContainingReference;
00026       SerializedProperty reference;
00027     }
00028 
00034     public static void ReplaceSceneReferences<T>(T a, T b) where T : UnityObject {
00035       var aId = a.GetInstanceID();
00036       var refType = typeof(T);
00037       
00038       var curScene = SceneManager.GetActiveScene();
00039       var rootObjs = curScene.GetRootGameObjects();
00040       foreach (var rootObj in rootObjs) {
00041         var transforms = rootObj.GetComponentsInChildren<Transform>();
00042         foreach (var transform in transforms) {
00043           var components = transform.GetComponents<Component>();
00044 
00045           var objectChanges = new List<Action>();
00046           foreach (var component in components) {
00047             var compType = typeof(Component);
00048             var fieldInfos = compType.GetFields(BindingFlags.Instance
00049               | BindingFlags.FlattenHierarchy | BindingFlags.NonPublic
00050               | BindingFlags.Public);
00051             foreach (var fieldInfo in fieldInfos.Query()
00052               .Where(fi => fi.FieldType.IsAssignableFrom(refType))) {
00053               var refValue = fieldInfo.GetValue(component) as T;
00054               if (refValue.GetInstanceID() == aId) {
00055                 objectChanges.Add(() => {
00056                   fieldInfo.SetValue(fieldInfo, b);
00057                 });
00058               }
00059             }
00060           }
00061           if (objectChanges.Count > 0) {
00062             Undo.RecordObject(transform.gameObject,
00063               "Swap " + transform.name + " references from " + a.name + " to " + b.name);
00064             foreach (var change in objectChanges) {
00065               change();
00066             }
00067           }
00068         }
00069       }
00070     }
00071 
00072   }
00073 
00074 }
