00010 using UnityEngine;
00011 using System.Collections;
00012 
00013 namespace Leap.Unity{
00014   [ExecuteInEditMode]
00015   public class EnableDepthBuffer : MonoBehaviour {
00016     public const string DEPTH_TEXTURE_VARIANT_NAME = "USE_DEPTH_TEXTURE";
00017   
00018     [SerializeField]
00019     private DepthTextureMode _depthTextureMode = DepthTextureMode.Depth;
00020   
00021     void Awake() {
00022       GetComponent<Camera>().depthTextureMode = _depthTextureMode;
00023   
00024       if (SystemInfo.SupportsRenderTextureFormat(RenderTextureFormat.Depth) &&
00025           _depthTextureMode != DepthTextureMode.None) {
00026         Shader.EnableKeyword(DEPTH_TEXTURE_VARIANT_NAME);
00027       } else {
00028         Shader.DisableKeyword(DEPTH_TEXTURE_VARIANT_NAME);
00029       }
00030     }
00031   }
00032 }
