00010 using System;
00011 using System.Collections.Generic;
00012 using UnityEngine;
00013 using UnityEngine.Events;
00014 
00015 namespace Leap.Unity {
00016 
00017   [Serializable]
00018   public class EnumEventTable : ISerializationCallbackReceiver {
00019 
00020     [Serializable]
00021     private class Entry {
00022 
00023       [SerializeField]
00024       public int enumValue;
00025 
00026       [SerializeField]
00027       public UnityEvent callback;
00028     }
00029 
00030     //The actual serialized list of entries
00031     [SerializeField]
00032     private List<Entry> _entries = new List<Entry>();
00033 
00034     //Not serialized, is just populated after deserialization and used
00035     //when calling Invoke for speed.
00036     private Dictionary<int, UnityEvent> _entryMap = new Dictionary<int, UnityEvent>();
00037 
00038     public bool HasUnityEvent(int enumValue) {
00039       UnityEvent callback;
00040       if (_entryMap.TryGetValue(enumValue, out callback)) {
00041         return callback.GetPersistentEventCount() > 0;
00042       }
00043       else {
00044         return false;
00045       }
00046     }
00047 
00048     public void Invoke(int enumValue) {
00049       UnityEvent callback;
00050       if (_entryMap.TryGetValue(enumValue, out callback)) {
00051         callback.Invoke();
00052       }
00053     }
00054 
00055     public void OnBeforeSerialize() { }
00056 
00057     public void OnAfterDeserialize() {
00058       if (_entryMap == null) {
00059         _entryMap = new Dictionary<int, UnityEvent>();
00060       } else {
00061         _entryMap.Clear();
00062       }
00063 
00064       foreach (var entry in _entries) {
00065         _entryMap[entry.enumValue] = entry.callback;
00066       }
00067     }
00068   }
00069 }
