00020   public static class IInteractionBehaviourExtensions {
00021 
00022     public static bool ShouldIgnoreHover(this IInteractionBehaviour intObj, InteractionController controller) {
00023       switch (intObj.ignoreHoverMode) {
00024         case IgnoreHoverMode.None: return false;
00025         case IgnoreHoverMode.Left: return  !controller.isTracked || controller.isLeft;
00026         case IgnoreHoverMode.Right: return !controller.isTracked || controller.isRight;
00027         case IgnoreHoverMode.Both: default: return true;
00028       }
00029     }
00030 
00031   }
