00037   public static class Vector3Extensions {
00038 
00039     public static Vector3 ConstrainToSegment(this Vector3 position, Vector3 a, Vector3 b) {
00040       Vector3 ba = b - a;
00041       return Vector3.Lerp(a, b, Vector3.Dot(position - a, ba) / ba.sqrMagnitude);
00042     }
00043 
00044     public static float LargestComp(this Vector3 v) {
00045       return Mathf.Max(Mathf.Max(v.x, v.y), v.z);
00046     }
00047 
00048     public static int LargestCompIdx(this Vector3 v) {
00049       return v.x > v.y ?
00050                v.x > v.z ?
00051                  0 // x > y, x > z
00052                : 2 // x > y, z > x
00053              : v.y > v.z ?
00054                1   // y > x, y > z
00055              : 2;  // y > x, z > y
00056     }
00057 
00058     public static Vector3 Abs(this Vector3 v) {
00059       return new Vector3(Mathf.Abs(v.x), Mathf.Abs(v.y), Mathf.Abs(v.z));
00060     }
00061 
00062   }
