section.programlisting:
00001 //======= Copyright (c) Valve Corporation, All rights reserved. ===============
00002 //
00003 // Purpose: Controls for the non-VR debug camera
00004 //
00005 //=============================================================================
00006 
00007 using UnityEngine;
00008 using System.Collections;
00009 
00010 namespace Valve.VR.InteractionSystem
00011 {
00012     //-------------------------------------------------------------------------
00013     [RequireComponent( typeof( Camera ) )]
00014     public class FallbackCameraController : MonoBehaviour
00015     {
00016         public float speed = 4.0f;
00017         public float shiftSpeed = 16.0f;
00018         public bool showInstructions = true;
00019 
00020         private Vector3 startEulerAngles;
00021         private Vector3 startMousePosition;
00022         private float realTime;
00023 
00024         //-------------------------------------------------
00025         void OnEnable()
00026         {
00027             realTime = Time.realtimeSinceStartup;
00028         }
00029 
00030 
00031         //-------------------------------------------------
00032         void Update()
00033         {
00034             float forward = 0.0f;
00035             if ( Input.GetKey( KeyCode.W ) || Input.GetKey( KeyCode.UpArrow ) )
00036             {
00037                 forward += 1.0f;
00038             }
00039             if ( Input.GetKey( KeyCode.S ) || Input.GetKey( KeyCode.DownArrow ) )
00040             {
00041                 forward -= 1.0f;
00042             }
00043 
00044             float right = 0.0f;
00045             if ( Input.GetKey( KeyCode.D ) || Input.GetKey( KeyCode.RightArrow ) )
00046             {
00047                 right += 1.0f;
00048             }
00049             if ( Input.GetKey( KeyCode.A ) || Input.GetKey( KeyCode.LeftArrow ) )
00050             {
00051                 right -= 1.0f;
00052             }
00053 
00054             float currentSpeed = speed;
00055             if ( Input.GetKey( KeyCode.LeftShift ) || Input.GetKey( KeyCode.RightShift ) )
00056             {
00057                 currentSpeed = shiftSpeed;
00058             }
00059 
00060             float realTimeNow = Time.realtimeSinceStartup;
00061             float deltaRealTime = realTimeNow - realTime;
00062             realTime = realTimeNow;
00063 
00064             Vector3 delta = new Vector3( right, 0.0f, forward ) * currentSpeed * deltaRealTime;
00065 
00066             transform.position += transform.TransformDirection( delta );
00067 
00068             Vector3 mousePosition = Input.mousePosition;
00069 
00070             if ( Input.GetMouseButtonDown( 1 ) /* right mouse */)
00071             {
00072                 startMousePosition = mousePosition;
00073                 startEulerAngles = transform.localEulerAngles;
00074             }
00075 
00076             if ( Input.GetMouseButton( 1 ) /* right mouse */)
00077             {
00078                 Vector3 offset = mousePosition - startMousePosition;
00079                 transform.localEulerAngles = startEulerAngles + new Vector3( -offset.y * 360.0f / Screen.height, offset.x * 360.0f / Screen.width, 0.0f );
00080             }
00081         }
00082 
00083 
00084         //-------------------------------------------------
00085         void OnGUI()
00086         {
00087             if ( showInstructions )
00088             {
00089                 GUI.Label( new Rect( 10.0f, 10.0f, 600.0f, 400.0f ),
00090                     "WASD/Arrow Keys to translate the camera\n" +
00091                     "Right mouse click to rotate the camera\n" +
00092                     "Left mouse click for standard interactions.\n" );
00093             }
00094         }
00095     }
00096 }
