00010 using UnityEngine;
00011 #if UNITY_EDITOR
00012 using UnityEditor;
00013 #endif
00014 
00015 namespace Leap.Unity {
00016 
00017   public static class GuiRectUtil {
00018 
00019     public static Vector3 Corner00(this Rect rect) {
00020       return new Vector3(rect.x, rect.y);
00021     }
00022 
00023     public static Vector3 Corner10(this Rect rect) {
00024       return new Vector3(rect.x + rect.width, rect.y);
00025     }
00026 
00027     public static Vector3 Corner01(this Rect rect) {
00028       return new Vector3(rect.x, rect.y + rect.height);
00029     }
00030 
00031     public static Vector3 Corner11(this Rect rect) {
00032       return new Vector3(rect.x + rect.width, rect.y + rect.height);
00033     }
00034 
00035     public static Rect Encapsulate(this Rect rect, Vector2 point) {
00036       if (point.x < rect.x) {
00037         rect.width += rect.x - point.x;
00038         rect.x = point.x;
00039       } else if (point.x > rect.x + rect.width) {
00040         rect.width = point.x - rect.x;
00041       }
00042 
00043       if (point.y < rect.y) {
00044         rect.height += rect.y - point.y;
00045         rect.y = point.y;
00046       } else if (point.y > rect.y + rect.height) {
00047         rect.height = point.y - rect.y;
00048       }
00049 
00050       return rect;
00051     }
00052 
00053     public static void SplitHorizontally(this Rect rect, out Rect left, out Rect right) {
00054       left = rect;
00055       left.width /= 2;
00056       right = left;
00057       right.x += right.width;
00058     }
00059 
00060     public static void SplitHorizontallyWithRight(this Rect rect, out Rect left, out Rect right, float rightWidth) {
00061       left = rect;
00062       left.width -= rightWidth;
00063       right = left;
00064       right.x += right.width;
00065       right.width = rightWidth;
00066     }
00067 
00068     public static Rect NextLine(this Rect rect) {
00069       rect.y += rect.height;
00070       return rect;
00071     }
00072 
00073     public static Rect FromRight(this Rect rect, float width) {
00074       rect.x = rect.width - width;
00075       rect.width = width;
00076       return rect;
00077     }
00078 
00079 #if UNITY_EDITOR
00080     public static Rect SingleLine(this Rect rect) {
00081       rect.height = EditorGUIUtility.singleLineHeight;
00082       return rect;
00083     }
00084 
00085     public static Rect Indent(this Rect rect) {
00086       rect.x += EditorGUIUtility.singleLineHeight;
00087       rect.width -= EditorGUIUtility.singleLineHeight;
00088       return rect;
00089     }
00090 #endif
00091   }
00092 }
