00010 using UnityEngine;
00011 using System.Collections;
00012 
00013 namespace Leap.Unity {
00015   public class HandDrop : HandTransitionBehavior {
00016     private Vector3 startingPalmPosition;
00017     private Quaternion startingOrientation;
00018     private Transform palm;
00019 
00020     // Use this for initialization
00021     protected override void Awake() {
00022       base.Awake();
00023       palm = GetComponent<HandModel>().palm;
00024       startingPalmPosition = palm.localPosition;
00025       startingOrientation = palm.localRotation;
00026     }
00027 
00028     protected override void HandFinish() {
00029       StartCoroutine(LerpToStart());
00030     }
00031     protected override void HandReset() {
00032       StopAllCoroutines();
00033     }
00034 
00035     private IEnumerator LerpToStart() {
00036       Vector3 droppedPosition = palm.localPosition;
00037       Quaternion droppedOrientation = palm.localRotation;
00038       float duration = .25f;
00039       float startTime = Time.time;
00040       float endTime = startTime + duration;
00041 
00042       while (Time.time <= endTime) {
00043         float t = (Time.time - startTime) / duration;
00044         palm.localPosition = Vector3.Lerp(droppedPosition, startingPalmPosition, t);
00045         palm.localRotation = Quaternion.Lerp(droppedOrientation, startingOrientation, t);
00046         yield return null;
00047       }
00048     }
00049   }
00050 }
