00533         private static ChangeAction[] GenerateChangeActions(int count)
00534         {
00535             var entries = new ChangeAction[count];
00536             for (var i = 0; i < count; i++)
00537                 entries[i] = new ChangeAction(path: "Path" + i, action: "Action" + i);
00538             return entries;
00539         }
