00010 using UnityEngine;
00011 using UnityEditor;
00012 using System.Linq;
00013 using System.Collections.Generic;
00014 
00015 namespace Leap.Unity {
00016 
00017   public static class Hotkeys {
00018 
00019     [MenuItem("GameObject/Make Group %g")]
00020     public static void MakeGroup() {
00021       if (!CorePreferences.allowGroupObjectsHotkey) {
00022         return;
00023       }
00024 
00025       GameObject[] objs = Selection.GetFiltered<GameObject>(SelectionMode.ExcludePrefab | SelectionMode.OnlyUserModifiable | SelectionMode.Editable);
00026       if (objs.Length == 0) {
00027         return;
00028       }
00029 
00030       Transform first = objs[0].transform;
00031 
00032       List<Transform> hierarchy = new List<Transform>();
00033 
00034       Transform parent = first.parent;
00035       while (parent != null) {
00036         hierarchy.Add(parent);
00037         parent = parent.parent;
00038       }
00039 
00040       int index = 0;
00041       parent = hierarchy.FirstOrDefault();
00042 
00043       if (parent != null) {
00044         foreach (var obj in objs) {
00045           Transform t = obj.transform;
00046           while (!t.IsChildOf(parent) || t == parent) {
00047             index++;
00048             if (index >= hierarchy.Count) {
00049               parent = null;
00050               break;
00051             } else {
00052               parent = hierarchy[index];
00053             }
00054           }
00055           if (parent == null) {
00056             break;
00057           }
00058         }
00059       }
00060 
00061       GameObject root = new GameObject("Group");
00062       root.transform.SetParent(parent);
00063       root.transform.localPosition = Vector3.zero;
00064       root.transform.localRotation = Quaternion.identity;
00065       root.transform.localScale = Vector3.one;
00066       Undo.RegisterCreatedObjectUndo(root, "Created group object.");
00067 
00068       List<Transform> allTransforms = new List<Transform>();
00069       if (parent == null) {
00070         var sceneRoots = root.scene.GetRootGameObjects();
00071         foreach (var sceneRoot in sceneRoots) {
00072           allTransforms.AddRange(sceneRoot.GetComponentsInChildren<Transform>());
00073         }
00074       } else {
00075         allTransforms.AddRange(parent.GetComponentsInChildren<Transform>());
00076       }
00077 
00078       foreach (var obj in allTransforms) {
00079         if (objs.Contains(obj.gameObject)) {
00080           Transform originalParent = obj.transform.parent;
00081           obj.transform.SetParent(root.transform, worldPositionStays: true);
00082 
00083           Vector3 newPos = obj.transform.localPosition;
00084           Quaternion newRot = obj.transform.localRotation;
00085           Vector3 newScale = obj.transform.localScale;
00086 
00087           obj.transform.SetParent(originalParent, worldPositionStays: true);
00088           Undo.SetTransformParent(obj.transform, root.transform, "Moved " + obj.name + " into group.");
00089           Undo.RecordObject(obj.transform, "Set new transform for " + obj.name + ".");
00090 
00091           obj.transform.localPosition = newPos;
00092           obj.transform.localRotation = newRot;
00093           obj.transform.localScale = newScale;
00094         }
00095       }
00096 
00097       Selection.activeGameObject = root;
00098       Undo.CollapseUndoOperations(Undo.GetCurrentGroup());
00099     }
00100 
00101     [MenuItem("GameObject/Reset Local Transform %e")]
00102     public static void ResetAll() {
00103       if (!CorePreferences.allowClearTransformHotkey) {
00104         return;
00105       }
00106 
00107       GameObject[] objs = Selection.GetFiltered<GameObject>(SelectionMode.ExcludePrefab | SelectionMode.OnlyUserModifiable | SelectionMode.Editable);
00108       foreach (var obj in objs) {
00109         Undo.RecordObject(obj.transform, "Cleared transform for " + obj.name + ".");
00110         obj.transform.localPosition = Vector3.zero;
00111         obj.transform.localRotation = Quaternion.identity;
00112         obj.transform.localScale = Vector3.one;
00113       }
00114     }
00115 
00116     [MenuItem("GameObject/Reset Local Position and Rotation %#e")]
00117     public static void ResetPositionRotation() {
00118       if (!CorePreferences.allowClearTransformHotkey) {
00119         return;
00120       }
00121 
00122       GameObject[] objs = Selection.GetFiltered<GameObject>(SelectionMode.ExcludePrefab | SelectionMode.OnlyUserModifiable | SelectionMode.Editable);
00123       foreach (var obj in objs) {
00124         Undo.RecordObject(obj.transform, "Cleared local position and rotation for " + obj.name + ".");
00125         obj.transform.localPosition = Vector3.zero;
00126         obj.transform.localRotation = Quaternion.identity;
00127       }
00128     }
00129   }
