00010 using System.Collections;
00011 using System.Collections.Generic;
00012 using UnityEngine;
00013 
00014 namespace Leap.Unity.Examples {
00015 
00026   public class IgnoreCollisionsInChildren : MonoBehaviour {
00027 
00028     void Start() {
00029       IgnoreCollisionsInChildrenOf(this.transform);
00030     }
00031 
00032     public static void IgnoreCollisionsInChildrenOf(Transform t, bool ignore = true) {
00033       var colliders = Pool<List<Collider>>.Spawn();
00034       try {
00035         t.GetComponentsInChildren<Collider>(true, colliders);
00036 
00037         for (int i = 0; i < colliders.Count; i++) {
00038           for (int j = 0; j < colliders.Count; j++) {
00039             if (i == j) continue;
00040 
00041             Physics.IgnoreCollision(colliders[i], colliders[j], ignore);
00042           }
00043         }
00044       }
00045       finally {
00046         colliders.Clear();
00047         Pool<List<Collider>>.Recycle(colliders);
00048       }
00049     }
00050 
00051   }
00052 
00053 }
