00084     public static float SignedAngle(this Vector3 a, Vector3 b, Vector3 axis) {
00085       float sign = Vector3.Dot(Vector3.Cross(a,b), axis) < 0f ? -1f : 1f;
00086       return sign * Vector3.Angle(a, b);
00087     }
