00089         protected virtual void Start()
00090         {
00091             highlightMat = (Material)Resources.Load("SteamVR_HoverHighlight", typeof(Material));
00092 
00093             if (highlightMat == null)
00094                 Debug.LogError("<b>[SteamVR Interaction]</b> Hover Highlight Material is missing. Please create a material named 'SteamVR_HoverHighlight' and place it in a Resources folder");
00095 
00096             if (skeletonPoser != null)
00097             {
00098                 if (useHandObjectAttachmentPoint)
00099                 {
00100                     //Debug.LogWarning("<b>[SteamVR Interaction]</b> SkeletonPose and useHandObjectAttachmentPoint both set at the same time. Ignoring useHandObjectAttachmentPoint.");
00101                     useHandObjectAttachmentPoint = false;
00102                 }
00103             }
00104         }
00105 
00106         protected virtual bool ShouldIgnoreHighlight(Component component)
00107         {
00108             return ShouldIgnore(component.gameObject);
00109         }
00110 
00111         protected virtual bool ShouldIgnore(GameObject check)
00112         {
00113             for (int ignoreIndex = 0; ignoreIndex < hideHighlight.Length; ignoreIndex++)
00114             {
00115                 if (check == hideHighlight[ignoreIndex])
00116                     return true;
00117             }
00118 
00119             return false;
00120         }
00121 
00122         protected virtual void CreateHighlightRenderers()
00123         {
00124             existingSkinnedRenderers = this.GetComponentsInChildren<SkinnedMeshRenderer>(true);
00125             highlightHolder = new GameObject("Highlighter");
00126             highlightSkinnedRenderers = new SkinnedMeshRenderer[existingSkinnedRenderers.Length];
00127 
00128             for (int skinnedIndex = 0; skinnedIndex < existingSkinnedRenderers.Length; skinnedIndex++)
00129             {
00130                 SkinnedMeshRenderer existingSkinned = existingSkinnedRenderers[skinnedIndex];
00131 
00132                 if (ShouldIgnoreHighlight(existingSkinned))
00133                     continue;
00134 
00135                 GameObject newSkinnedHolder = new GameObject("SkinnedHolder");
00136                 newSkinnedHolder.transform.parent = highlightHolder.transform;
00137                 SkinnedMeshRenderer newSkinned = newSkinnedHolder.AddComponent<SkinnedMeshRenderer>();
00138                 Material[] materials = new Material[existingSkinned.sharedMaterials.Length];
00139                 for (int materialIndex = 0; materialIndex < materials.Length; materialIndex++)
00140                 {
00141                     materials[materialIndex] = highlightMat;
00142                 }
00143 
00144                 newSkinned.sharedMaterials = materials;
00145                 newSkinned.sharedMesh = existingSkinned.sharedMesh;
00146                 newSkinned.rootBone = existingSkinned.rootBone;
00147                 newSkinned.updateWhenOffscreen = existingSkinned.updateWhenOffscreen;
00148                 newSkinned.bones = existingSkinned.bones;
00149 
00150                 highlightSkinnedRenderers[skinnedIndex] = newSkinned;
00151             }
00152 
00153             MeshFilter[] existingFilters = this.GetComponentsInChildren<MeshFilter>(true);
00154             existingRenderers = new MeshRenderer[existingFilters.Length];
00155             highlightRenderers = new MeshRenderer[existingFilters.Length];
00156 
00157             for (int filterIndex = 0; filterIndex < existingFilters.Length; filterIndex++)
00158             {
00159                 MeshFilter existingFilter = existingFilters[filterIndex];
00160                 MeshRenderer existingRenderer = existingFilter.GetComponent<MeshRenderer>();
00161 
00162                 if (existingFilter == null || existingRenderer == null || ShouldIgnoreHighlight(existingFilter))
00163                     continue;
00164 
00165                 GameObject newFilterHolder = new GameObject("FilterHolder");
00166                 newFilterHolder.transform.parent = highlightHolder.transform;
00167                 MeshFilter newFilter = newFilterHolder.AddComponent<MeshFilter>();
00168                 newFilter.sharedMesh = existingFilter.sharedMesh;
00169                 MeshRenderer newRenderer = newFilterHolder.AddComponent<MeshRenderer>();
00170 
00171                 Material[] materials = new Material[existingRenderer.sharedMaterials.Length];
00172                 for (int materialIndex = 0; materialIndex < materials.Length; materialIndex++)
00173                 {
00174                     materials[materialIndex] = highlightMat;
00175                 }
00176                 newRenderer.sharedMaterials = materials;
00177 
00178                 highlightRenderers[filterIndex] = newRenderer;
00179                 existingRenderers[filterIndex] = existingRenderer;
00180             }
00181         }
00182 
00183         protected virtual void UpdateHighlightRenderers()
00184         {
00185             if (highlightHolder == null)
00186                 return;
00187 
00188             for (int skinnedIndex = 0; skinnedIndex < existingSkinnedRenderers.Length; skinnedIndex++)
00189             {
00190                 SkinnedMeshRenderer existingSkinned = existingSkinnedRenderers[skinnedIndex];
00191                 SkinnedMeshRenderer highlightSkinned = highlightSkinnedRenderers[skinnedIndex];
00192 
00193                 if (existingSkinned != null && highlightSkinned != null && attachedToHand == false)
00194                 {
00195                     highlightSkinned.transform.position = existingSkinned.transform.position;
00196                     highlightSkinned.transform.rotation = existingSkinned.transform.rotation;
00197                     highlightSkinned.transform.localScale = existingSkinned.transform.lossyScale;
00198                     highlightSkinned.localBounds = existingSkinned.localBounds;
00199                     highlightSkinned.enabled = isHovering && existingSkinned.enabled && existingSkinned.gameObject.activeInHierarchy;
00200 
00201                     int blendShapeCount = existingSkinned.sharedMesh.blendShapeCount;
00202                     for (int blendShapeIndex = 0; blendShapeIndex < blendShapeCount; blendShapeIndex++)
00203                     {
00204                         highlightSkinned.SetBlendShapeWeight(blendShapeIndex, existingSkinned.GetBlendShapeWeight(blendShapeIndex));
00205                     }
00206                 }
00207                 else if (highlightSkinned != null)
00208                     highlightSkinned.enabled = false;
00209 
00210             }
00211 
00212             for (int rendererIndex = 0; rendererIndex < highlightRenderers.Length; rendererIndex++)
00213             {
00214                 MeshRenderer existingRenderer = existingRenderers[rendererIndex];
00215                 MeshRenderer highlightRenderer = highlightRenderers[rendererIndex];
00216 
00217                 if (existingRenderer != null && highlightRenderer != null && attachedToHand == false)
00218                 {
00219                     highlightRenderer.transform.position = existingRenderer.transform.position;
00220                     highlightRenderer.transform.rotation = existingRenderer.transform.rotation;
00221                     highlightRenderer.transform.localScale = existingRenderer.transform.lossyScale;
00222                     highlightRenderer.enabled = isHovering && existingRenderer.enabled && existingRenderer.gameObject.activeInHierarchy;
00223                 }
00224                 else if (highlightRenderer != null)
00225                     highlightRenderer.enabled = false;
00226             }
00227         }
00228 
00232         protected virtual void OnHandHoverBegin(Hand hand)
00233         {
00234             wasHovering = isHovering;
00235             isHovering = true;
00236 
00237             hoveringHand = hand;
00238 
00239             if (highlightOnHover == true)
00240             {
00241                 CreateHighlightRenderers();
00242                 UpdateHighlightRenderers();
00243             }
00244         }
00245 
00246 
00250         private void OnHandHoverEnd(Hand hand)
00251         {
00252             wasHovering = isHovering;
00253             isHovering = false;
00254 
00255             if (highlightOnHover && highlightHolder != null)
00256                 Destroy(highlightHolder);
00257         }
00258 
00259         protected virtual void Update()
00260         {
00261             if (highlightOnHover)
00262             {
00263                 UpdateHighlightRenderers();
00264 
00265                 if (isHovering == false && highlightHolder != null)
00266                     Destroy(highlightHolder);
00267             }
00268         }
00269         
00270 
00271         protected float blendToPoseTime = 0.1f;
00272         protected float releasePoseBlendTime = 0.2f;
00273 
00274         protected virtual void OnAttachedToHand(Hand hand)
00275         {
00276             if (activateActionSetOnAttach != null)
00277                 activateActionSetOnAttach.Activate(hand.handType);
00278 
00279             if (onAttachedToHand != null)
00280             {
00281                 onAttachedToHand.Invoke(hand);
00282             }
00283 
00284             if (skeletonPoser != null && hand.skeleton != null)
00285             {
00286                 hand.skeleton.BlendToPoser(skeletonPoser, blendToPoseTime);
00287             }
00288 
00289             attachedToHand = hand;
00290         }
00291 
00292         protected virtual void OnDetachedFromHand(Hand hand)
00293         {
00294             if (activateActionSetOnAttach != null)
00295             {
00296                 if (hand.otherHand == null || hand.otherHand.currentAttachedObjectInfo.HasValue == false ||
00297                     (hand.otherHand.currentAttachedObjectInfo.Value.interactable != null &&
00298                      hand.otherHand.currentAttachedObjectInfo.Value.interactable.activateActionSetOnAttach != this.activateActionSetOnAttach))
00299                 {
00300                     activateActionSetOnAttach.Deactivate(hand.handType);
00301                 }
00302             }
00303 
00304             if (onDetachedFromHand != null)
00305             {
00306                 onDetachedFromHand.Invoke(hand);
00307             }
00308 
00309 
00310             if (skeletonPoser != null)
00311             {
00312                 if (hand.skeleton != null)
00313                     hand.skeleton.BlendToSkeleton(releasePoseBlendTime);
00314             }
00315 
00316             attachedToHand = null;
00317         }
00318 
00319         protected virtual void OnDestroy()
00320         {
00321             isDestroying = true;
00322 
00323             if (attachedToHand != null)
00324             {
00325                 attachedToHand.DetachObject(this.gameObject, false);
00326                 attachedToHand.skeleton.BlendToSkeleton(0.1f);
00327             }
00328             
00329             if (highlightHolder != null)
00330                 Destroy(highlightHolder);
00331             
00332         }
00333 
00334 
00335         protected virtual void OnDisable()
00336         {
00337             isDestroying = true;
00338 
00339             if (attachedToHand != null)
00340             {
00341                 attachedToHand.ForceHoverUnlock();
00342             }
00343 
00344             if (highlightHolder != null)
00345                 Destroy(highlightHolder);
00346         }
00347     }
