00001 //======= Copyright (c) Valve Corporation, All rights reserved. ===============
00002 //
00003 // Purpose: Demonstrates how to create a simple interactable object
00004 //
00005 //=============================================================================
00006 
00007 using UnityEngine;
00008 using System.Collections;
00009 
00010 namespace Valve.VR.InteractionSystem.Sample
00011 {
00012     //-------------------------------------------------------------------------
00013     [RequireComponent( typeof( Interactable ) )]
00014     public class InteractableExample : MonoBehaviour
00015     {
00016         private TextMesh generalText;
00017         private TextMesh hoveringText;
00018         private Vector3 oldPosition;
00019         private Quaternion oldRotation;
00020 
00021         private float attachTime;
00022 
00023         private Hand.AttachmentFlags attachmentFlags = Hand.defaultAttachmentFlags & ( ~Hand.AttachmentFlags.SnapOnAttach ) & (~Hand.AttachmentFlags.DetachOthers) & (~Hand.AttachmentFlags.VelocityMovement);
00024 
00025         private Interactable interactable;
00026 
00027         //-------------------------------------------------
00028         void Awake()
00029         {
00030             var textMeshs = GetComponentsInChildren<TextMesh>();
00031             generalText = textMeshs[0];
00032             hoveringText = textMeshs[1];
00033 
00034             generalText.text = "No Hand Hovering";
00035             hoveringText.text = "Hovering: False";
00036 
00037             interactable = this.GetComponent<Interactable>();
00038         }
00039 
00040 
00041         //-------------------------------------------------
00042         // Called when a Hand starts hovering over this object
00043         //-------------------------------------------------
00044         private void OnHandHoverBegin( Hand hand )
00045         {
00046             generalText.text = "Hovering hand: " + hand.name;
00047         }
00048 
00049 
00050         //-------------------------------------------------
00051         // Called when a Hand stops hovering over this object
00052         //-------------------------------------------------
00053         private void OnHandHoverEnd( Hand hand )
00054         {
00055             generalText.text = "No Hand Hovering";
00056         }
00057 
00058 
00059         //-------------------------------------------------
00060         // Called every Update() while a Hand is hovering over this object
00061         //-------------------------------------------------
00062         private void HandHoverUpdate( Hand hand )
00063         {
00064             GrabTypes startingGrabType = hand.GetGrabStarting();
00065             bool isGrabEnding = hand.IsGrabEnding(this.gameObject);
00066 
00067             if (interactable.attachedToHand == null && startingGrabType != GrabTypes.None)
00068             {
00069                 // Save our position/rotation so that we can restore it when we detach
00070                 oldPosition = transform.position;
00071                 oldRotation = transform.rotation;
00072 
00073                 // Call this to continue receiving HandHoverUpdate messages,
00074                 // and prevent the hand from hovering over anything else
00075                 hand.HoverLock(interactable);
00076 
00077                 // Attach this object to the hand
00078                 hand.AttachObject(gameObject, startingGrabType, attachmentFlags);
00079             }
00080             else if (isGrabEnding)
00081             {
00082                 // Detach this object from the hand
00083                 hand.DetachObject(gameObject);
00084 
00085                 // Call this to undo HoverLock
00086                 hand.HoverUnlock(interactable);
00087 
00088                 // Restore position/rotation
00089                 transform.position = oldPosition;
00090                 transform.rotation = oldRotation;
00091             }
00092         }
00093 
00094 
00095         //-------------------------------------------------
00096         // Called when this GameObject becomes attached to the hand
00097         //-------------------------------------------------
00098         private void OnAttachedToHand( Hand hand )
00099         {
00100             generalText.text = string.Format("Attached: {0}", hand.name);
00101             attachTime = Time.time;
00102         }
00103         
00104 
00105 
00106         //-------------------------------------------------
00107         // Called when this GameObject is detached from the hand
00108         //-------------------------------------------------
00109         private void OnDetachedFromHand( Hand hand )
00110         {
00111             generalText.text = string.Format("Detached: {0}", hand.name);
00112         }
00113 
00114 
00115         //-------------------------------------------------
00116         // Called every Update() while this GameObject is attached to the hand
00117         //-------------------------------------------------
00118         private void HandAttachedUpdate( Hand hand )
00119         {
00120             generalText.text = string.Format("Attached: {0} :: Time: {1:F2}", hand.name, (Time.time - attachTime));
00121         }
00122 
00123         private bool lastHovering = false;
00124         private void Update()
00125         {
00126             if (interactable.isHovering != lastHovering) //save on the .tostrings a bit
00127             {
00128                 hoveringText.text = string.Format("Hovering: {0}", interactable.isHovering);
00129                 lastHovering = interactable.isHovering;
00130             }
00131         }
00132 
00133 
00134         //-------------------------------------------------
00135         // Called when this attached GameObject becomes the primary attached object
00136         //-------------------------------------------------
00137         private void OnHandFocusAcquired( Hand hand )
00138         {
00139         }
00140 
00141 
00142         //-------------------------------------------------
00143         // Called when another attached GameObject becomes the primary attached object
00144         //-------------------------------------------------
00145         private void OnHandFocusLost( Hand hand )
00146         {
00147         }
00148     }
00149 }
