00771     protected virtual void OnEnable() {
00772       if (manager == null) {
00773         manager = InteractionManager.instance;
00774 
00775         if (manager == null) {
00776           Debug.LogError("Interaction Behaviours require an Interaction Manager. Please "
00777                        + "ensure you have an InteractionManager in your scene.");
00778           this.enabled = false;
00779         }
00780       }
00781 
00782       if (manager != null && !manager.IsBehaviourRegistered(this)) {
00783         manager.RegisterInteractionBehaviour(this);
00784       }
00785 
00786       // Make sure we have a list of all of this object's colliders.
00787       RefreshInteractionColliders();
00788 
00789       // Refresh curved space. Currently a maximum of one (1) LeapSpace is supported per
00790       // InteractionBehaviour.
00791       foreach (var collider in _interactionColliders) {
00792         var leapSpace = collider.transform.GetComponentInParent<ISpaceComponent>();
00793         if (leapSpace != null) {
00794           space = leapSpace;
00795           break;
00796         }
00797       }
00798 
00799       // Ensure physics layers are set up properly.
00800       initLayers();
00801     }
00802 
00803     protected virtual void OnDisable() {
00804       // Remove this object's layer tracking from the manager.
00805       finalizeLayers();
00806 
00807       if (manager != null && manager.IsBehaviourRegistered(this)) {
00808         manager.UnregisterInteractionBehaviour(this);
00809       }
00810     }
00811 
00812     protected virtual void Start() {
00813       // Check any Joint attachments to automatically be able to choose Kabsch pivot
00814       // setting (grasping).
00815       RefreshPositionLockedState();
00816     }
