00833     private HashSet<InteractionController> _hoveringControllers = new HashSet<InteractionController>();
00834 
00835     private InteractionController _closestHoveringController = null;
00836     private float _closestHoveringControllerDistance = float.PositiveInfinity;
00837     private InteractionHand _closestHoveringHand = null;
00838 
00848     public virtual float GetHoverDistance(Vector3 worldPosition) {
00849       float closestComparativeColliderDistance = float.PositiveInfinity;
00850       bool hasColliders = false;
00851       float testDistance = float.PositiveInfinity;
00852 
00853       if (rigidbody == null) {
00854         // The Interaction Object is probably being destroyed, or is otherwise in an
00855         // invalid state.
00856         return float.PositiveInfinity;
00857       }
00858 
00859       foreach (var collider in _interactionColliders) {
00860         if (!hasColliders) hasColliders = true;
00861 
00862         if (collider is MeshCollider) {
00863           // Native, faster ClosestPoint, but no support for off-center colliders; use to
00864           // support MeshColliders.
00865           testDistance = (Physics.ClosestPoint(worldPosition,
00866                                                collider,
00867                                                collider.attachedRigidbody.position,
00868                                                collider.attachedRigidbody.rotation)
00869                           - worldPosition).magnitude;
00870         }
00871         // Custom, slower ClosestPoint
00872         else {
00873           // Note: Should be using rigidbody position instead of transform; this will
00874           // cause problems when colliders are moving very fast (one-frame delay).
00875           testDistance = (collider.transform.TransformPoint(
00876                             collider.ClosestPointOnSurface(
00877                               collider.transform.InverseTransformPoint(worldPosition)))
00878                           - worldPosition).magnitude;
00879         }
00880 
00881         if (testDistance < closestComparativeColliderDistance) {
00882           closestComparativeColliderDistance = testDistance;
00883         }
00884       }
00885 
00886       if (!hasColliders) {
00887         return (this.rigidbody.position - worldPosition).magnitude;
00888       }
00889       else {
00890         return closestComparativeColliderDistance;
00891       }
00892     }
00893 
00894     public void BeginHover(List<InteractionController> controllers) {
00895       foreach (var controller in controllers) {
00896         _hoveringControllers.Add(controller);
00897       }
00898 
00899       refreshClosestHoveringController();
00900 
00901       foreach (var controller in controllers) {
00902         OnPerControllerHoverBegin(controller);
00903       }
00904 
00905       if (_hoveringControllers.Count == controllers.Count) {
00906         OnHoverBegin();
00907       }
00908     }
00909 
00910     public void EndHover(List<InteractionController> controllers) {
00911       foreach (var controller in controllers) {
00912         _hoveringControllers.Remove(controller);
00913       }
00914 
00915       refreshClosestHoveringController();
00916 
00917       foreach (var controller in controllers) {
00918         OnPerControllerHoverEnd(controller);
00919       }
00920 
00921       if (_hoveringControllers.Count == 0) {
00922         OnHoverEnd();
00923       }
00924     }
00925 
00926     public void StayHovered(List<InteractionController> controllers) {
00927       refreshClosestHoveringController();
00928       OnHoverStay();
00929     }
00930 
00931     private void refreshClosestHoveringController() {
00932       float closestControllerDistance = float.PositiveInfinity;
00933       _closestHoveringController = getClosestController(_hoveringControllers,
00934                                                         out closestControllerDistance);
00935       _closestHoveringControllerDistance = closestControllerDistance;
00936 
00937       float closestHandDistance = float.PositiveInfinity;
00938       _closestHoveringHand = getClosestController(_hoveringControllers,
00939                                                   out closestHandDistance,
00940                                                   controller => controller.intHand != null)
00941                                as InteractionHand;
00942       // closestHandDistance unused for now.
00943     }
00944 
00945     private InteractionController getClosestController(HashSet<InteractionController> controllers,
00946                                                        out float closestDistance,
00947                                                        Func<InteractionController, bool> filter = null) {
00948       InteractionController closestHoveringController = null;
00949       float closestHoveringControllerDist = float.PositiveInfinity;
00950       foreach (var controller in controllers) {
00951         if (filter != null && filter(controller) == false) continue;
00952 
00953         float distance = GetHoverDistance(controller.hoverPoint);
00954         if (closestHoveringHand == null
00955             || distance < closestHoveringControllerDist) {
00956           closestHoveringController = controller;
00957           closestHoveringControllerDist = distance;
00958         }
00959       }
00960 
00961       closestDistance = closestHoveringControllerDist;
00962       return closestHoveringController;
00963     }
00964 
00973     public void ClearHoverTracking(bool onlyInvalidControllers = false) {
00974       var tempControllers = Pool<HashSet<InteractionController>>.Spawn();
00975       try {
00976         foreach (var controller in hoveringControllers) {
00977           if (onlyInvalidControllers && this.ShouldIgnoreHover(controller)) {
00978             tempControllers.Add(controller);
00979           }
00980         }
00981 
00982         foreach (var controller in tempControllers) {
00983           controller.ClearHoverTrackingForObject(this);
00984         }
00985       }
00986       finally {
00987         tempControllers.Clear();
00988         Pool<HashSet<InteractionController>>.Recycle(tempControllers);
00989       }
00990     }
00991 
00992     private HashSet<InteractionController> _primaryHoveringControllers = new HashSet<InteractionController>();
00993     private InteractionController _closestPrimaryHoveringController = null;
00994     private InteractionHand _closestPrimaryHoveringHand = null;
00995 
00996     public void BeginPrimaryHover(List<InteractionController> controllers) {
00997       foreach (var controller in controllers) {
00998         _primaryHoveringControllers.Add(controller);
00999       }
01000 
01001       refreshClosestPrimaryHoveringController();
01002 
01003       foreach (var controller in controllers) {
01004         OnPerControllerPrimaryHoverBegin(controller);
01005       }
01006 
01007       if (_primaryHoveringControllers.Count == controllers.Count) {
01008         OnPrimaryHoverBegin();
01009       }
01010     }
01011 
01012     public void EndPrimaryHover(List<InteractionController> controllers) {
01013       foreach (var controller in controllers) {
01014         _primaryHoveringControllers.Remove(controller);
01015       }
01016 
01017       refreshClosestPrimaryHoveringController();
01018 
01019       foreach (var controller in controllers) {
01020         OnPerControllerPrimaryHoverEnd(controller);
01021       }
01022 
01023       if (_primaryHoveringControllers.Count == 0) {
01024         OnPrimaryHoverEnd();
01025       }
01026     }
01027 
01028     public void StayPrimaryHovered(List<InteractionController> controllers) {
