01033     private void refreshClosestPrimaryHoveringController() {
01034       _closestPrimaryHoveringController = getClosestPrimaryHoveringController();
01035       _closestPrimaryHoveringHand = getClosestPrimaryHoveringController((controller) => controller.intHand != null) as InteractionHand;
01036     }
01037 
01038     private InteractionController getClosestPrimaryHoveringController(Func<InteractionController, bool> filter = null) {
01039       InteractionController closestController = null;
01040       float closestDist = float.PositiveInfinity;
01041       foreach (var controller in _primaryHoveringControllers) {
01042         if (filter != null && filter(controller) == false) continue;
01043 
01044         if (closestController == null || controller.primaryHoverDistance < closestDist) {
01045           closestController = controller;
01046           closestDist = controller.primaryHoverDistance;
01047         }
01048       }
01049       return closestController;
01050     }
01056     public void ClearPrimaryHoverTracking() {
01057       var tempControllers = Pool<HashSet<InteractionController>>.Spawn();
01058       try {
01059         foreach (var controller in primaryHoveringControllers) {
01060           tempControllers.Add(controller);
01061         }
01062 
01063         foreach (var controller in tempControllers) {
01064           controller.ClearPrimaryHoverTracking();
01065         }
01066       }
01067       finally {
01068         tempControllers.Clear();
01069         Pool<HashSet<InteractionController>>.Recycle(tempControllers);
01070       }
01071     }
01072 
01097     public List<Collider> primaryHoverColliders {
01098       get { return _interactionColliders; }
01099     }
