01107     public void BeginContact(List<InteractionController> controllers) {
01108       foreach (var controller in controllers) {
01109         _contactingControllers.Add(controller);
01110 
01111         OnPerControllerContactBegin(controller);
01112       }
01113 
01114       if (_contactingControllers.Count == controllers.Count) {
01115         OnContactBegin();
01116       }
01117     }
