01119     public void EndContact(List<InteractionController> controllers) {
01120       foreach (var controller in controllers) {
01121         _contactingControllers.Remove(controller);
01122 
01123         OnPerControllerContactEnd(controller);
01124       }
01125 
01126       if (_contactingControllers.Count == 0) {
01127         OnContactEnd();
01128       }
01129     }
