01140     public void ClearContactTracking() {
01141       var tempControllers = Pool<HashSet<InteractionController>>.Spawn();
01142       try {
01143         foreach (var controller in contactingControllers) {
01144           tempControllers.Add(controller);
01145         }
01146 
01147         foreach (var controller in tempControllers) {
01148           controller.ClearContactTrackingForObject(this);
01149         }
01150       }
01151       finally {
01152         tempControllers.Clear();
01153         Pool<HashSet<InteractionController>>.Recycle(tempControllers);
01154       }
01155     }
