01159     #region Grasping
01160 
01161     private HashSet<InteractionController> _graspingControllers = new HashSet<InteractionController>();
01162     
01163     private bool _wasKinematicBeforeGrasp;
01164     private bool _justGrasped = false;
01165 
01166     private float _dragBeforeGrasp = 0F;
01167     private float _angularDragBeforeGrasp = 0.05F;
01168 
01169     private IGraspedPoseHandler _graspedPoseHandler;
01171     public IGraspedPoseHandler graspedPoseHandler {
01172       get {
01173         if (_graspedPoseHandler == null) {
01174           _graspedPoseHandler = new KabschGraspedPose(this);
01175         }
01176         return _graspedPoseHandler;
01177       }
01178       set {
01179         _graspedPoseHandler = value;
01180       }
01181     }
