01203     private IThrowHandler _throwHandler;
01205     public IThrowHandler throwHandler {
01206       get {
01207         if (_throwHandler == null) {
01208           _throwHandler = new SlidingWindowThrow();
01209         }
01210         return _throwHandler;
01211       }
01212       set {
01213         _throwHandler = value;
01214       }
01215     }
01216 
01217     public void BeginGrasp(List<InteractionController> controllers) {
01218       _justGrasped = true;
01219 
01220       // End suspension by ending the grasp on the suspending hand,
01221       // calling EndGrasp immediately.
01222       if (isSuspended) {
01223         _suspendingController.ReleaseGrasp();
01224       }
01225 
01226       // If multi-grasp is not allowed, release the old grasp.
01227       if (!allowMultiGrasp && isGrasped) {
01228         _graspingControllers.Query().First().ReleaseGrasp();
01229       }
01230 
01231       // Add each newly grasping hand to internal reference and pose solver.
01232       foreach (var controller in controllers) {
01233         _graspingControllers.Add(controller);
01234 
01235         if (moveObjectWhenGrasped) {
01236           graspedPoseHandler.AddController(controller);
01237         }
01238 
01239         // Fire interaction callback.
01240         OnPerControllerGraspBegin(controller);
01241       }
01242 
01243       // If object wasn't grasped before, store rigidbody settings and
01244       // fire object interaction callback.
01245       if (_graspingControllers.Count == controllers.Count) {
01246 
01247         // Remember drag settings pre-grasp, to be restored on release.
01248         _dragBeforeGrasp = rigidbody.drag;
01249         _angularDragBeforeGrasp = rigidbody.angularDrag;
01250 
01251         // Remember kinematic state.
01252         _wasKinematicBeforeGrasp = rigidbody.isKinematic;
01253         switch (graspedMovementType) {
01254           case GraspedMovementType.Inherit: break; // no change
01255           case GraspedMovementType.Kinematic:
01256             rigidbody.isKinematic = true; break;
01257           case GraspedMovementType.Nonkinematic:
01258             rigidbody.isKinematic = false; break;
01259         }
01260 
01261         // Set rigidbody drag/angular drag to zero.
01262         rigidbody.drag = 0F;
01263         rigidbody.angularDrag = 0F;
01264 
01265         OnGraspBegin();
01266       }
01267     }
01268 
01269     public void EndGrasp(List<InteractionController> controllers) {
01270       if (_graspingControllers.Count == controllers.Count && isSuspended) {
01271         // No grasped hands: Should not be suspended any more;
01272         // having been suspended also means we were only grasped by one hand
01273         EndSuspension(controllers[0]);
01274       }
01275 
01276       foreach (var controller in controllers) {
01277         _graspingControllers.Remove(controller);
01278 
01279         // Fire interaction callback.
01280         OnPerControllerGraspEnd(controller);
01281 
01282         if (moveObjectWhenGrasped) {
01283           // Remove each hand from the pose solver.
01284           graspedPoseHandler.RemoveController(controller);
01285         }
01286       }
01287 
01288       // If the object is no longer grasped by any hands, restore state and
01289       // activate throw handler.
01290       if (_graspingControllers.Count == 0) {
01291         // Restore drag settings from prior to the grasp.
01292         rigidbody.drag = _dragBeforeGrasp;
01293         rigidbody.angularDrag = _angularDragBeforeGrasp;
01294 
01295         // Revert kinematic state.
01296         rigidbody.isKinematic = _wasKinematicBeforeGrasp;
01297 
01298         if (controllers.Count == 1) {
01299           throwHandler.OnThrow(this, controllers.Query().First());
01300         }
01301 
01302         OnGraspEnd();
01303 
01304         if (_justGrasped) _justGrasped = false;
01305       }
01306     }
01307 
01308     public void StayGrasped(List<InteractionController> controllers) {
01309       if (moveObjectWhenGrasped) {
01310         Vector3    origPosition = rigidbody.position;
01311         Quaternion origRotation = rigidbody.rotation;
01312         Vector3    newPosition;
01313         Quaternion newRotation;
01314 
01315         graspedPoseHandler.GetGraspedPosition(out newPosition, out newRotation);
01316 
01317         fixedUpdateGraspedMovement(new Pose(origPosition, origRotation),
01318                                    new Pose(newPosition, newRotation),
01319                                    controllers);
01320 
01321         throwHandler.OnHold(this, controllers);
01322       }
01323 
01324       OnGraspStay();
01325 
01326       _justGrasped = false;
01327     }
01328 
01329     protected virtual void fixedUpdateGraspedMovement(Pose origPose, Pose newPose,
01330                                               List<InteractionController> controllers) {
01331       IGraspedMovementHandler graspedMovementHandler
01332           = rigidbody.isKinematic ?
01333               (IGraspedMovementHandler)_kinematicGraspedMovement
01334             : (IGraspedMovementHandler)_nonKinematicGraspedMovement;
01335       graspedMovementHandler.MoveTo(newPose.position, newPose.rotation,
01336                                     this, _justGrasped);
01337 
01338       OnGraspedMovement(origPose.position, origPose.rotation,
01339                         newPose.position, newPose.rotation,
01340                         controllers);
01341     }
01342 
01343     protected InteractionController _suspendingController = null;
01344 
01345     public void BeginSuspension(InteractionController controller) {
01346       _suspendingController = controller;
01347 
01348       OnSuspensionBegin(controller);
01349     }
01350 
01351     public void EndSuspension(InteractionController controller) {
01352       _suspendingController = null;
01353 
01354       OnSuspensionEnd(controller);
01355     }
