01359     #region Forces
01360 
01361     private bool _appliedForces = false;
01362     protected Vector3 _accumulatedLinearAcceleration = Vector3.zero;
01363     protected Vector3 _accumulatedAngularAcceleration = Vector3.zero;
01364 
01365     public void FixedUpdateForces() {
01366       if (!isGrasped) {
01367         //Only apply if non-zero to prevent waking up the body
01368         if (_accumulatedLinearAcceleration != Vector3.zero) {
01369           rigidbody.velocity += _accumulatedLinearAcceleration * Time.fixedDeltaTime;
01370         }
01371 
01372         if (_accumulatedAngularAcceleration != Vector3.zero) {
01373           rigidbody.angularVelocity += _accumulatedAngularAcceleration * Time.fixedDeltaTime;
01374         }
01375 
01376         //Reset so we can accumulate for the next frame
01377         _accumulatedLinearAcceleration = Vector3.zero;
01378         _accumulatedAngularAcceleration = Vector3.zero;
01379 
01380         _appliedForces = false;
01381       }
01382     }
