01399     public void RefreshInteractionColliders() {
01400       Utils.FindColliders<Collider>(this.gameObject, _interactionColliders,
01401                                     includeInactiveObjects: false);
01402 
01403       _interactionColliders.RemoveAll(
01404         c => c.GetComponent<IgnoreColliderForInteraction>() != null);
01405 
01406       // Since the interaction colliders might have changed, or appeared for the first
01407       // time, set their layers appropriately.
01408       refreshInteractionColliderLayers();
01409     }
