01413     #region Interaction Layers
01414 
01415     private int _lastInteractionLayer = -1;
01416     private int _lastNoContactLayer = -1;
01417 
01418     private void initLayers() {
01419       refreshInteractionLayer();
01420       refreshNoContactLayer();
01421 
01422       (manager as IInternalInteractionManager).NotifyIntObjAddedInteractionLayer(this, interactionLayer, false);
01423       (manager as IInternalInteractionManager).NotifyIntObjAddedNoContactLayer(this, noContactLayer, false);
01424       (manager as IInternalInteractionManager).RefreshLayersNow();
01425 
01426       _lastInteractionLayer = interactionLayer;
01427       _lastNoContactLayer = noContactLayer;
01428     }
01429 
01430     private void refreshInteractionLayer() {
01431       interactionLayer = overrideInteractionLayer ? this.interactionLayer
01432                                                   : manager.interactionLayer;
01433     }
01434 
01435     private void refreshNoContactLayer() {
01436       noContactLayer = overrideNoContactLayer ? this.noContactLayer
01437                                               : manager.interactionNoContactLayer;
01438     }
01439 
01440     private void fixedUpdateLayers() {
01441       using (new ProfilerSample("Interaction Behaviour: fixedUpdateLayers")) {
01442         int layer;
01443         refreshInteractionLayer();
01444         refreshNoContactLayer();
01445 
01446         // Update the object's layer based on interaction state.
01447         if (ignoreContact) {
01448           layer = noContactLayer;
01449         }
01450         else {
01451           if (isGrasped) {
01452             layer = noContactLayer;
01453           }
01454           else {
01455             layer = interactionLayer;
01456           }
01457         }
01458         if (this.gameObject.layer != layer) {
01459           this.gameObject.layer = layer;
01460 
01461           refreshInteractionColliderLayers();
01462         }
01463 
01464         // Update the manager if necessary.
01465 
01466         if (interactionLayer != _lastInteractionLayer) {
01467           (manager as IInternalInteractionManager).NotifyIntObjHasNewInteractionLayer(this, oldInteractionLayer: _lastInteractionLayer,
01468                                                                                             newInteractionLayer: interactionLayer);
01469           _lastInteractionLayer = interactionLayer;
01470         }
01471 
01472         if (noContactLayer != _lastNoContactLayer) {
01473           (manager as IInternalInteractionManager).NotifyIntObjHasNewNoContactLayer(this, oldNoContactLayer: _lastNoContactLayer,
01474                                                                                           newNoContactLayer: noContactLayer);
01475           _lastNoContactLayer = noContactLayer;
01476         }
01477       }
01478     }
01479 
01480     private void finalizeLayers() {
01481       (manager as IInternalInteractionManager).NotifyIntObjRemovedInteractionLayer(this, interactionLayer, false);
01482       (manager as IInternalInteractionManager).NotifyIntObjRemovedNoContactLayer(this, noContactLayer, false);
01483       (manager as IInternalInteractionManager).RefreshLayersNow();
01484     }
01485 
01496     private void refreshInteractionColliderLayers() {
01497       for (int i = 0; i < _interactionColliders.Count; i++) {
01498         if (_interactionColliders[i].gameObject.layer != this.gameObject.layer) {
01499           _interactionColliders[i].gameObject.layer = this.gameObject.layer;
01500         }
01501       }
01502     }
