01506     #region Locked Position (Joint) Checking
01507 
01508     private bool _isPositionLocked = false;
01509 
01522     public bool isPositionLocked { get { return _isPositionLocked; } }
01523 
01529     public void RefreshPositionLockedState() {
01530       if ((rigidbody.constraints & RigidbodyConstraints.FreezePositionX) > 0
01531        && (rigidbody.constraints & RigidbodyConstraints.FreezePositionY) > 0
01532        && (rigidbody.constraints & RigidbodyConstraints.FreezePositionZ) > 0) {
01533         _isPositionLocked = true;
01534         return;
01535       }
01536       else {
01537         _isPositionLocked = false;
01538 
01539         Joint[] joints = rigidbody.GetComponents<Joint>();
01540         foreach (var joint in joints) {
01541           if (joint.connectedBody == null || joint.connectedBody.isKinematic) {
01542             if (joint is FixedJoint) {
01543               _isPositionLocked = true;
01544               return;
01545             }
01546             if (joint is HingeJoint) {
01547               _isPositionLocked = true;
01548               return;
01549             }
01550             // if (joint is SpringJoint) {
01551             // no check required; spring joints never fully lock position.
01552             // }
01553             if (joint is CharacterJoint) {
01554               _isPositionLocked = true;
01555               return;
01556             }
01557             ConfigurableJoint configJoint = joint as ConfigurableJoint;
01558             if (configJoint != null
01559               && (configJoint.xMotion == ConfigurableJointMotion.Locked
01560                 || (configJoint.xMotion == ConfigurableJointMotion.Limited
01561                   && configJoint.linearLimit.limit == 0F))
01562               && (configJoint.yMotion == ConfigurableJointMotion.Locked
01563                 || (configJoint.yMotion == ConfigurableJointMotion.Limited
01564                   && configJoint.linearLimit.limit == 0F))
01565               && (configJoint.zMotion == ConfigurableJointMotion.Locked
01566                 || (configJoint.zMotion == ConfigurableJointMotion.Limited
01567                   && configJoint.linearLimit.limit == 0F))) {
01568               _isPositionLocked = true;
01569               return;
01570             }
01571           }
01572         }
01573       }
01574     }
