01612     private void InitUnityEvents() {
01613       // If the interaction component is added at runtime, _eventTable won't have been
01614       // constructed yet.
01615       if (_eventTable == null) _eventTable = new EnumEventTable();
01616 
01617       setupCallback(ref OnHoverBegin,                     EventType.HoverBegin);
01618       setupCallback(ref OnHoverEnd,                       EventType.HoverEnd);
01619       setupCallback(ref OnHoverStay,                      EventType.HoverStay);
01620       setupCallback(ref OnPerControllerHoverBegin,        EventType.PerControllerHoverBegin);
01621       setupCallback(ref OnPerControllerHoverEnd,          EventType.PerControllerHoverEnd);
01622 
01623       setupCallback(ref OnPrimaryHoverBegin,              EventType.PrimaryHoverBegin);
01624       setupCallback(ref OnPrimaryHoverEnd,                EventType.PrimaryHoverEnd);
01625       setupCallback(ref OnPrimaryHoverStay,               EventType.PrimaryHoverStay);
01626       setupCallback(ref OnPerControllerPrimaryHoverBegin, EventType.PerControllerPrimaryHoverBegin);
01627       setupCallback(ref OnPerControllerPrimaryHoverEnd,   EventType.PerControllerPrimaryHoverEnd);
01628 
01629       setupCallback(ref OnGraspBegin,                     EventType.GraspBegin);
01630       setupCallback(ref OnGraspEnd,                       EventType.GraspEnd);
01631       setupCallback(ref OnGraspStay,                      EventType.GraspStay);
01632       setupCallback(ref OnPerControllerGraspBegin,        EventType.PerControllerGraspBegin);
01633       setupCallback(ref OnPerControllerGraspEnd,          EventType.PerControllerGraspEnd);
01634 
01635       setupCallback(ref OnSuspensionBegin,                EventType.SuspensionBegin);
01636       setupCallback(ref OnSuspensionEnd,                  EventType.SuspensionEnd);
01637 
01638       setupCallback(ref OnContactBegin,                   EventType.ContactBegin);
01639       setupCallback(ref OnContactEnd,                     EventType.ContactEnd);
01640       setupCallback(ref OnContactStay,                    EventType.ContactStay);
01641       setupCallback(ref OnPerControllerContactBegin,      EventType.PerControllerContactBegin);
01642       setupCallback(ref OnPerControllerContactEnd,        EventType.PerControllerContactEnd);
01643     }
01644 
01645     private void setupCallback(ref Action action, EventType type) {
01646       if (_eventTable.HasUnityEvent((int)type)) {
01647         action += () => _eventTable.Invoke((int)type);
01648       }
01649       else {
01650         action += () => { };
01651       }
01652     }
01653 
01654     private void setupCallback<T>(ref Action<T> action, EventType type) {
01655       if (_eventTable.HasUnityEvent((int)type)) {
01656         action += (h) => _eventTable.Invoke((int)type);
01657       }
01658       else {
01659         action += (h) => { };
01660       }
01661     }
