00010 using Leap.Unity.Query;
00011 using UnityEditor;
00012 using UnityEngine;
00013 
00014 namespace Leap.Unity.Interaction {
00015 
00016   [CanEditMultipleObjects]
00017   [CustomEditor(typeof(InteractionBehaviour), editorForChildClasses: true)]
00018   public class InteractionBehaviourEditor : CustomEditorBase<InteractionBehaviour> {
00019 
00020     private EnumEventTableEditor _tableEditor;
00021 
00022     protected override void OnEnable() {
00023       base.OnEnable();
00024 
00025       // Interaction Manager hookup.
00026       specifyCustomDecorator("_manager", drawInteractionManagerDecorator);
00027 
00028       deferProperty("_eventTable");
00029       specifyCustomDrawer("_eventTable", drawEventTable);
00030 
00031       specifyConditionalDrawing(() => !targets.Query().All(intObj => intObj.ignoreContact),
00032                                 "_contactForceMode");
00033 
00034       specifyConditionalDrawing(() => !targets.Query().All(intObj => intObj.ignoreGrasping),
00035                                 "_allowMultiGrasp",
00036                                 "_moveObjectWhenGrasped",
00037                                 "graspedMovementType");
00038 
00039       // Layer Overrides
00040       specifyConditionalDrawing(() => targets.Query().Any(intObj => intObj.overrideInteractionLayer),
00041                                 "_interactionLayer");
00042       specifyCustomDecorator("_interactionLayer", drawInteractionLayerDecorator);
00043       specifyConditionalDrawing(() => targets.Query().Any(intObj => intObj.overrideNoContactLayer),
00044                                 "_noContactLayer");
00045       specifyCustomDecorator("_noContactLayer", drawNoContactLayerDecorator);
00046     }
00047 
00048     private void drawInteractionManagerDecorator(SerializedProperty property) {
00049       bool shouldDrawInteractionManagerNotSetWarning = false;
00050       foreach (var target in targets) {
00051         if (PrefabUtility.GetPrefabType(target) == PrefabType.Prefab) continue;
00052 
00053         if (target.manager == null) {
00054           shouldDrawInteractionManagerNotSetWarning = true;
00055           break;
00056         }
00057       }
00058       if (shouldDrawInteractionManagerNotSetWarning) {
00059         bool pluralTargets = targets.Length > 1;
00060         string noManagerSetWarningMessage = "";
00061         if (pluralTargets) {
00062           noManagerSetWarningMessage = "One or more of the currently selected interaction "
00063                                      + "objects doesn't have its Interaction Manager set. ";
00064         }
00065         else {
00066           noManagerSetWarningMessage = "The currently selected interaction object doesn't "
00067                                      + "have its Interaction Manager set. ";
00068         }
00069         noManagerSetWarningMessage += " Object validation requires a configured manager "
00070                                     + "property.";
00071 
00072         drawSetManagerWarningBox(noManagerSetWarningMessage, MessageType.Error);
00073       }
00074     }
00075 
00076     private void drawSetManagerWarningBox(string warningMessage, MessageType messageType) {
00077       EditorGUILayout.BeginHorizontal();
00078 
00079       EditorGUILayout.HelpBox(warningMessage, messageType);
00080 
00081       Rect buttonRect = EditorGUILayout.GetControlRect(GUILayout.MaxWidth(100F),
00082                                                        GUILayout.ExpandHeight(true),
00083                                                        GUILayout.MaxHeight(40F));
00084       InteractionManager manager = InteractionManager.instance;
00085       EditorGUI.BeginDisabledGroup(manager == null);
00086       if (GUI.Button(buttonRect, new GUIContent("Auto-Fix",
00087                                   manager == null ? "Please add an Interaction Manager to "
00088                                                   + "your scene."
00089                                               : "Use InteractionManager.instance to "
00090                                               + "automatically set the manager of the "
00091                                               + "selected interaction objects."))) {
00092         if (manager == null) {
00093           Debug.LogError("Attempt to find an InteractionManager instance failed. Is there "
00094                        + "an InteractionManager in your scene?");
00095         }
00096         else {
00097           foreach (var target in targets) {
00098             if (PrefabUtility.GetPrefabType(target) == PrefabType.Prefab) continue;
00099 
00100             Undo.RecordObject(target, "Auto-set Interaction Manager");
00101             target.manager = manager;
00102           }
00103         }
00104       }
00105       EditorGUI.EndDisabledGroup();
00106 
00107       EditorGUILayout.EndHorizontal();
00108 
00109       EditorGUILayout.Space();
00110     }
00111 
00112     private void drawInteractionLayerDecorator(SerializedProperty property) {
00113       bool shouldDrawWarning = false;
00114       foreach (var target in targets) {
00115         if (target.manager == null) continue; // Can't check.
00116 
00117         if (target.overrideInteractionLayer
00118             && !target.manager.autoGenerateLayers
00119             && Physics.GetIgnoreLayerCollision(target.interactionLayer.layerIndex,
00120                                                target.manager.contactBoneLayer.layerIndex)) {
00121 
00122           shouldDrawWarning = true;
00123           break;
00124         }
00125       }
00126 
00127       if (shouldDrawWarning) {
00128         bool pluralTargets = targets.Length > 1;
00129         string message;
00130         if (pluralTargets) {
00131           message = "One or more of the selected interaction objects has its "
00132                   + "Interaction layer set NOT to collide with the contact bone "
00133                   + "layer. ";
00134         }
00135         else {
00136           message = "The selected interaction object has its Interaction layer set "
00137                   + "NOT to collide with the contact bone layer. ";
00138         }
00139         message += "This will prevent an interaction object from supporting contact with "
00140                  + "any hands or controllers.";
00141 
00142         EditorGUILayout.HelpBox(message, MessageType.Warning);
00143       }
00144     }
00145 
00146     private void drawNoContactLayerDecorator(SerializedProperty property) {
00147       bool shouldDrawCollisionWarning = false;
00148       foreach (var target in targets) {
00149         if (target.manager == null) continue; // Can't check.
00150 
00151         if (target.overrideNoContactLayer
00152             && !target.manager.autoGenerateLayers
00153             && !Physics.GetIgnoreLayerCollision(target.noContactLayer.layerIndex,
00154                                                 target.manager.contactBoneLayer.layerIndex)) {
00155           shouldDrawCollisionWarning = true;
00156           break;
00157         }
00158       }
00159 
00160       if (shouldDrawCollisionWarning) {
00161         bool pluralTargets = targets.Length > 1;
00162         string noContactErrorMessage;
00163         if (pluralTargets) {
00164           noContactErrorMessage = "One or more selected interaction objects has its No "
00165                                   + "Contact layer set to collide with the contact bone "
00166                                   + "layer. ";
00167         }
00168         else {
00169           noContactErrorMessage = "This interaction object has its No Contact layer set "
00170                                   + "to collide with the contact bone layer. ";
00171         }
00172 
00173         noContactErrorMessage += "Please ensure the Interaction Manager's contact bone "
00174                                  + "layer is set not to collide with any interaction "
00175                                  + "object's No Contact layer.";
00176 
00177         EditorGUILayout.HelpBox(noContactErrorMessage, MessageType.Error);
00178       }
00179     }
00180 
00181     public override void OnInspectorGUI() {
00182       checkHasColliders();
00183       
00184       base.OnInspectorGUI();
00185     }
00186 
00187     private void checkHasColliders() {
00188       bool anyMissingColliders = false;
00189       foreach (var singleTarget in targets) {
00190         if (singleTarget.GetComponentsInChildren<Collider>().Length == 0) {
00191           anyMissingColliders = true; break;
00192         }
00193       }
00194 
00195       if (anyMissingColliders) {
00196         bool pluralObjects = targets.Length > 1;
00197 
00198         string message;
00199         if (pluralObjects) {
00200           message = "One or more of the currently selected interaction objects have no "
00201                   + "colliders. Interaction objects without any Colliders cannot be "
00202                   + "interacted with.";
00203         }
00204         else {
00205           message = "This interaction object has no Colliders. Interaction objects "
00206                   + "without any Colliders cannot be interacted with.";
00207         }
00208 
00209         EditorGUILayout.HelpBox(message, MessageType.Warning);
00210       }
00211     }
00212 
00213     private void drawEventTable(SerializedProperty property) {
00214       if (_tableEditor == null) {
00215         _tableEditor = new EnumEventTableEditor(property, typeof(InteractionBehaviour.EventType));
00216       }
00217 
00218       _tableEditor.DoGuiLayout();
00219     }
00220   }
