00010 using Leap.Unity.Query;
00011 using System.Collections;
00012 using System.Collections.Generic;
00013 using UnityEditor;
00014 using UnityEngine;
00015 
00016 namespace Leap.Unity.Interaction {
00017 
00018   [CanEditMultipleObjects]
00019   [CustomEditor(typeof(InteractionController), true)]
00020   public class InteractionControllerEditor : CustomEditorBase<InteractionController> {
00021 
00022     public override void OnInspectorGUI() {
00023       checkParentedToManager();
00024       checkWithinHandModelManager();
00025       checkPrimaryHoverPoints();
00026 
00027       base.OnInspectorGUI();
00028     }
00029 
00030     private void checkPrimaryHoverPoints() {
00031       bool anyPrimaryHoverPoints = false;
00032       bool anyWithNoPrimaryHoverPoints = false;
00033       foreach (var singleTarget in targets) {
00034         anyPrimaryHoverPoints = false;
00035         foreach (var primaryHoverPoint in singleTarget.primaryHoverPoints) {
00036           if (primaryHoverPoint != null) {
00037             anyPrimaryHoverPoints = true;
00038             break;
00039           }
00040         }
00041 
00042         if (singleTarget.intHand != null) {
00043           for (int i = 0; i < singleTarget.intHand.enabledPrimaryHoverFingertips.Length; i++) {
00044             if (singleTarget.intHand.enabledPrimaryHoverFingertips[i]) {
00045               anyPrimaryHoverPoints = true;
00046               break;
00047             }
00048           }
00049         }
00050 
00051         if (!anyPrimaryHoverPoints) {
00052           anyWithNoPrimaryHoverPoints = true;
00053           break;
00054         }
00055       }
00056 
00057       if (anyWithNoPrimaryHoverPoints) {
00058         string message = "No primary hover points found for this interaction controller. "
00059                        + "This controller will never trigger primary hover for an object. "
00060                        + "UI elements such as InteractionButton and InteractionSlider "
00061                        + "will not be able to interact with this interaction controller.";
00062         EditorGUILayout.HelpBox(message, MessageType.Warning);
00063       }
00064     }
00065 
00066     private void checkParentedToManager() {
00067       bool plural = targets.Length > 1;
00068       bool anyNotParentedToInteractionManager;
00069 
00070       anyNotParentedToInteractionManager = targets.Query()
00071                                                   .Any(c => c.GetComponentInParent<InteractionManager>() == null);
00072 
00073       if (anyNotParentedToInteractionManager) {
00074         string message = "";
00075         if (plural) {
00076           message += "One of more currently selected controllers ";
00077         }
00078         else {
00079           message += "The currently selected controller ";
00080         }
00081 
00082         message += "is not the child of an Interaction Manager. Interaction Controllers "
00083                  + "must be childed to an Interaction Manager in order to function.";
00084 
00085         EditorGUILayout.HelpBox(message, MessageType.Warning);
00086       }
00087     }
00088 
00089     private void checkWithinHandModelManager() {
00090       bool plural = targets.Length > 1;
00091       bool anyWithinHandPool;
00092 
00093       HandModelManager handModelManager = FindObjectOfType<HandModelManager>();
00094       if (handModelManager == null) return;
00095 
00096       anyWithinHandPool = targets.Query()
00097                                  .Any(c => c.transform.parent == handModelManager.transform);
00098 
00099       if (anyWithinHandPool) {
00100         string message = "";
00101         if (plural) {
00102           message += "One or more of the currently selected controllers ";
00103         }
00104         else {
00105           message += "The currently selected controller ";
00106         }
00107 
00108         message += "is inside a HandModelManager. Interaction controllers, such "
00109                  + "as InteractionHands, are not HandModels and are not spawned by the "
00110                  + "HandModelManager. InteractionHands and all Interaction controllers "
00111                  + "should be childed to the Interaction Manager.";
00112 
00113         EditorGUILayout.HelpBox(message, MessageType.Error);
00114       }
00115     }
00116 
00117   }
00118 
00119 }
