00045     [Header("Horizontal Axis")]
00046     public float defaultHorizontalValue;
00047 
00048     [Tooltip("The minimum and maximum values that the slider reports on the horizontal axis.")]
00049     [FormerlySerializedAs("horizontalValueRange")]
00050     [SerializeField]
00051     private Vector2 _horizontalValueRange = new Vector2(0f, 1f);
00052     public float minHorizontalValue {
00053       get {
00054         return _horizontalValueRange.x;
00055       }
00056       set {
00057         if (value != _horizontalValueRange.x) {
00058           _horizontalValueRange.x = value;
00059           HorizontalSlideEvent(HorizontalSliderValue);
00060         }
00061       }
00062     }
00063 
00064     public float maxHorizontalValue {
00065       get {
00066         return _horizontalValueRange.y;
00067       }
00068       set {
00069         if (value != _horizontalValueRange.y) {
00070           _horizontalValueRange.y = value;
00071           HorizontalSlideEvent(HorizontalSliderValue);
00072         }
