00076     [Tooltip("The minimum and maximum horizontal extents that the slider can slide to in world space.")]
00077     [MinMax(-0.5f, 0.5f)]
00078     public Vector2 horizontalSlideLimits = new Vector2(-0.05f, 0.05f);
00079 
00080     [Tooltip("The number of discrete quantized notches **beyond the first** that this "
00081            + "slider can occupy on the horizontal axis. A value of zero indicates a "
