00139     public Action<float> HorizontalSlideEvent = (f) => { };
00140     public Action<float> VerticalSlideEvent = (f) => { };
00141 
00142     public float HorizontalSliderPercent {
00143       get {
00144         return _horizontalSliderPercent;
00145       }
00146       set {
00147         if (!_started) {
00148           Debug.LogWarning("An object is attempting to access this slider's value before it has been initialized!  Initializing now; this could yield unexpected behaviour...", this);
00149           Start();
00150         }
00151 
00152         _horizontalSliderPercent = value;
00153         localPhysicsPosition.x = Mathf.Lerp(initialLocalPosition.x + horizontalSlideLimits.x, initialLocalPosition.x + horizontalSlideLimits.y, _horizontalSliderPercent);
00154         physicsPosition = transform.parent.TransformPoint(localPhysicsPosition);
00155         rigidbody.position = physicsPosition;
00156       }
00157     }
00158 
00159     public float VerticalSliderPercent {
00160       get {
00161         return _verticalSliderPercent;
00162       }
00163       set {
00164         if (!_started) {
00165           Debug.LogWarning("An object is attempting to access this slider's value before it has been initialized!  Initializing now; this could yield unpected behaviour...", this);
00166           Start();
00167         }
00168 
00169         _verticalSliderPercent = value;
00170         localPhysicsPosition.y = Mathf.Lerp(initialLocalPosition.y + verticalSlideLimits.x, initialLocalPosition.y + verticalSlideLimits.y, _verticalSliderPercent);
00171         physicsPosition = transform.parent.TransformPoint(localPhysicsPosition);
00172         rigidbody.position = physicsPosition;
00173       }
00174     }
00175 
00177     public float HorizontalSliderValue {
00178       get {
00179         return Mathf.Lerp(_horizontalValueRange.x, _horizontalValueRange.y, _horizontalSliderPercent);
00180       }
00181       set {
00182         HorizontalSliderPercent = Mathf.InverseLerp(_horizontalValueRange.x, _horizontalValueRange.y, value);
00183       }
00184     }
00185 
00187     public float VerticalSliderValue {
00188       get {
00189         return Mathf.Lerp(_verticalValueRange.x, _verticalValueRange.y, _verticalSliderPercent);
00190       }
00191       set {
00192         VerticalSliderPercent = Mathf.InverseLerp(_verticalValueRange.x, _verticalValueRange.y, value);
00193       }
00194     }
00195 
00196     private void calculateSliderValues() {
00197       // Calculate renormalized slider values.
00198       if (horizontalSlideLimits.x != horizontalSlideLimits.y) {
00199         _horizontalSliderPercent = Mathf.InverseLerp(initialLocalPosition.x + horizontalSlideLimits.x, initialLocalPosition.x + horizontalSlideLimits.y, localPhysicsPosition.x);
00200         HorizontalSlideEvent(HorizontalSliderValue);
00201       }
00202 
00203       if (verticalSlideLimits.x != verticalSlideLimits.y) {
00204         _verticalSliderPercent = Mathf.InverseLerp(initialLocalPosition.y + verticalSlideLimits.x, initialLocalPosition.y + verticalSlideLimits.y, localPhysicsPosition.y);
00205         VerticalSlideEvent(VerticalSliderValue);
00206       }
00207     }
00208 
00209     public float normalizedHorizontalValue {
00210       get {
00211         return _horizontalSliderPercent;
00212       }
00213       set {
00214         var newValue = Mathf.Clamp01(value);
00215         if (newValue != _horizontalSliderPercent) {
00216           _horizontalSliderPercent = newValue;
00217           HorizontalSlideEvent(HorizontalSliderValue);
00218         }
00219       }
00220     }
00221 
00222     public float normalizedVerticalValue {
00223       get {
00224         return _verticalSliderPercent;
00225       }
00226       set {
00227         var newValue = Mathf.Clamp01(value);
00228         if (newValue != _verticalSliderPercent) {
00229           _verticalSliderPercent = newValue;
00230           VerticalSlideEvent(VerticalSliderValue);
00231         }
00232       }
00233     }
00234 
00241     public int horizontalStepValue {
00242       get {
00243         float range = _horizontalValueRange.y - _horizontalValueRange.x;
00244         if (range == 0F) return 0;
00245         else {
00246           return (int)(_horizontalSliderPercent * horizontalSteps * 1.001F);
00247         }
00248       }
00249     }
00250 
00254     public bool wasSlid {
00255       get { return _wasSlid && _sawWasSlid; }
00256     }
