00275     protected override void OnValidate() {
00276       base.OnValidate();
00277 
00278       if (this.transform.parent != null && this.transform.parent.GetComponent<RectTransform>() != null) {
00279         _parentHasRectTransform = true;
00280       }
00281       else {
00282         _parentHasRectTransform = false;
00283       }
00284     }
00285 
00286     protected override void OnEnable() {
00287       base.OnEnable();
00288 
00289       OnContactStay += calculateSliderValues;
00290     }
00291 
00292     protected override void OnDisable() {
00293       OnContactStay -= calculateSliderValues;
00294 
00295       base.OnDisable();
00296     }
00297 
00298     protected override void Start() {
00299       if (_started) return;
00300 
00301       _started = true;
00302 
00303       calculateSliderLimits();
00304 
00305       switch (sliderType) {
00306         case SliderType.Horizontal:
00307           verticalSlideLimits = new Vector2(0, 0);
00308           break;
00309         case SliderType.Vertical:
00310           horizontalSlideLimits = new Vector2(0, 0);
00311           break;
00312       }
00313 
00314       base.Start();
00315 
00316       HorizontalSlideEvent += _horizontalSlideEvent.Invoke;
00317       VerticalSlideEvent   += _verticalSlideEvent.Invoke;
00318 
00319       HorizontalSlideEvent += (f) => { _wasSlid = true; };
00320       VerticalSlideEvent   += (f) => { _wasSlid = true; };
00321 
00322       HorizontalSliderValue = defaultHorizontalValue;
00323       VerticalSliderValue = defaultVerticalValue;
00324 
00325       if (dispatchSlideValueOnStart) {
00326         HorizontalSlideEvent(HorizontalSliderValue);
00327         VerticalSlideEvent(VerticalSliderValue);
00328       }
00329     }
00330 
00331     protected override void Update() {
00332       base.Update();
00333 
00334       if (!Application.isPlaying) { return; }
00335 
00336       if (isPressed || isGrasped) {
00337         calculateSliderValues();
00338       }
00339 
00340       // Whenever "_wasSlid" is set to true (however many times between Update() cycles),
00341       // this logic produces a single Update() cycle through which slider.wasSlid
00342       // will return true. (This allows observing MonoBehaviours to simply check
00343       // "wasSlid" during their Update() cycle to perform updating logic.)
00344       if (_wasSlid && !_sawWasSlid) {
00345         _sawWasSlid = true;
00346       }
00347       else if (_sawWasSlid) {
00348         _wasSlid = false;
00349         _sawWasSlid = false;
00350       }
00351     }
