00363     #region Internal Methods
00364 
00365     private void calculateSliderLimits() {
00366       if (transform.parent != null) {
00367         parent = transform.parent.GetComponent<RectTransform>();
00368 
00369         if (overrideRectLimits) return;
00370 
00371         if (parent != null) {
00372           if (parent.rect.width < 0f || parent.rect.height < 0f) {
00373             Debug.LogError("Parent Rectangle dimensions negative; can't set slider boundaries!", parent.gameObject);
00374             enabled = false;
00375           } else {
00376             var self = transform.GetComponent<RectTransform>();
00377             if (self != null) {
00378               horizontalSlideLimits = new Vector2(parent.rect.xMin - transform.localPosition.x + self.rect.width / 2F, parent.rect.xMax - transform.localPosition.x - self.rect.width / 2F);
00379               if (horizontalSlideLimits.x > horizontalSlideLimits.y) {
00380                 horizontalSlideLimits = new Vector2(0F, 0F);
00381               }
00382               if (Mathf.Abs(horizontalSlideLimits.x) < 0.0001F) {
00383                 horizontalSlideLimits.x = 0F;
00384               }
00385               if (Mathf.Abs(horizontalSlideLimits.y) < 0.0001F) {
00386                 horizontalSlideLimits.y = 0F;
00387               }
00388 
00389               verticalSlideLimits = new Vector2(parent.rect.yMin - transform.localPosition.y + self.rect.height / 2F, parent.rect.yMax - transform.localPosition.y - self.rect.height / 2F);
00390               if (verticalSlideLimits.x > verticalSlideLimits.y) {
00391                 verticalSlideLimits = new Vector2(0F, 0F);
00392               }
00393               if (Mathf.Abs(verticalSlideLimits.x) < 0.0001F) {
00394                 verticalSlideLimits.x = 0F;
00395               }
00396               if (Mathf.Abs(verticalSlideLimits.y) < 0.0001F) {
00397                 verticalSlideLimits.y = 0F;
00398               }
00399             } else {
00400               horizontalSlideLimits = new Vector2(parent.rect.xMin - transform.localPosition.x, parent.rect.xMax - transform.localPosition.x);
00401               verticalSlideLimits = new Vector2(parent.rect.yMin - transform.localPosition.y, parent.rect.yMax - transform.localPosition.y);
00402             }
00403           }
00404         }
00405       }
00406     }
00407 
00408     protected override Vector3 constrainDepressedLocalPosition(Vector3 desiredOffset) {
00409       Vector3 unSnappedPosition =
00410         new Vector3(Mathf.Clamp((localPhysicsPosition.x + desiredOffset.x), initialLocalPosition.x + horizontalSlideLimits.x, initialLocalPosition.x + horizontalSlideLimits.y),
00411                     Mathf.Clamp((localPhysicsPosition.y + desiredOffset.y), initialLocalPosition.y + verticalSlideLimits.x, initialLocalPosition.y + verticalSlideLimits.y),
00412                                 (localPhysicsPosition.z + desiredOffset.z));
00413 
00414       float hSliderPercent = Mathf.InverseLerp(initialLocalPosition.x + horizontalSlideLimits.x, initialLocalPosition.x + horizontalSlideLimits.y, unSnappedPosition.x);
00415       if (horizontalSteps > 0) {
00416         hSliderPercent = Mathf.Round(hSliderPercent * (horizontalSteps)) / (horizontalSteps);
00417       }
00418 
00419       float vSliderPercent = Mathf.InverseLerp(initialLocalPosition.y + verticalSlideLimits.x, initialLocalPosition.y + verticalSlideLimits.y, unSnappedPosition.y);
00420       if (verticalSteps > 0) {
00421         vSliderPercent = Mathf.Round(vSliderPercent * (verticalSteps)) / (verticalSteps);
00422       }
00423 
00424       return new Vector3(Mathf.Lerp(initialLocalPosition.x + horizontalSlideLimits.x, initialLocalPosition.x + horizontalSlideLimits.y, hSliderPercent),
00425                          Mathf.Lerp(initialLocalPosition.y + verticalSlideLimits.x, initialLocalPosition.y + verticalSlideLimits.y, vSliderPercent),
00426                                    (localPhysicsPosition.z + desiredOffset.z));
00427     }
