00431     #region Gizmos
00432 
00433     protected override void OnDrawGizmosSelected() {
00434       base.OnDrawGizmosSelected();
00435 
00436       if (transform.parent != null) {
00437         Vector3 originPosition = Application.isPlaying ? initialLocalPosition : transform.localPosition;
00438         if (Application.isPlaying && startingPositionMode == StartingPositionMode.Relaxed) {
00439           originPosition = originPosition + Vector3.back * Mathf.Lerp(minMaxHeight.x, minMaxHeight.y, restingHeight);
00440         }
00441 
00442         // Actual slider slide limits
00443         Gizmos.color = Color.blue;
00444         Gizmos.DrawWireCube(originPosition +
00445           new Vector3((sliderType == SliderType.Vertical ? 0F : (horizontalSlideLimits.x + horizontalSlideLimits.y) * 0.5f),
00446                       (sliderType == SliderType.Horizontal ? 0F : (verticalSlideLimits.x + verticalSlideLimits.y) * 0.5f),
00447                       0f),
00448           new Vector3((sliderType == SliderType.Vertical ? 0F : horizontalSlideLimits.y - horizontalSlideLimits.x),
00449                       (sliderType == SliderType.Horizontal ? 0F : verticalSlideLimits.y - verticalSlideLimits.x),
00450                       0f));
00451 
00452         var self = GetComponent<RectTransform>();
00453         if (self != null) {
00454           // Apparent slide limits (against own rect limits)
00455           parent = transform.parent.GetComponent<RectTransform>();
00456           if (parent != null) {
00457             var parentRectHorizontal = new Vector2(parent.rect.xMin - originPosition.x, parent.rect.xMax - originPosition.x);
00458             var parentRectVertical = new Vector2(parent.rect.yMin - originPosition.y, parent.rect.yMax - originPosition.y);
00459             Gizmos.color = Color.Lerp(Color.blue, Color.cyan, 0.5F);
00460             Gizmos.DrawWireCube(originPosition +
00461               new Vector3((parentRectHorizontal.x + parentRectHorizontal.y) * 0.5f,
00462                           (parentRectVertical.x + parentRectVertical.y) * 0.5f,
00463                           (startingPositionMode == StartingPositionMode.Relaxed ?
00464                             Mathf.Lerp(minMaxHeight.x, minMaxHeight.y, 0.5F) - Mathf.Lerp(minMaxHeight.x, minMaxHeight.y, 1 - restingHeight)
00465                           : -1F * Mathf.Lerp(minMaxHeight.x, minMaxHeight.y, 0.5F))),
00466               new Vector3(parentRectHorizontal.y - parentRectHorizontal.x, parentRectVertical.y - parentRectVertical.x, (minMaxHeight.y - minMaxHeight.x)));
00467 
00468             // Own rect width/height
00469             Gizmos.color = Color.cyan;
00470             Gizmos.DrawWireCube(originPosition,
00471               self.rect.width * Vector3.right
00472             + self.rect.height * Vector3.up);
00473           }
00474         }
00475       }
00476     }
