00016 namespace Valve.VR.InteractionSystem
00017 {
00018     //-------------------------------------------------------------------------
00019     [RequireComponent( typeof( Interactable ) )]
00020     public class ItemPackageSpawner : MonoBehaviour
00021     {
00022         public ItemPackage itemPackage
00023         {
00024             get
00025             {
00026                 return _itemPackage;
00027             }
00028             set
00029             {
00030                 CreatePreviewObject();
00031             }
00032         }
00033 
00034         public ItemPackage _itemPackage;
00035 
00036         private bool useItemPackagePreview = true;
00037         private bool useFadedPreview = false;
00038         private GameObject previewObject;
00039 
00040         public bool requireGrabActionToTake = false;
00041         public bool requireReleaseActionToReturn = false;
00042         public bool showTriggerHint = false;
00043 
00044         [EnumFlags]
00045         public Hand.AttachmentFlags attachmentFlags = Hand.defaultAttachmentFlags;
00046 
00047         public bool takeBackItem = false; // if a hand enters this trigger and has the item this spawner dispenses at the top of the stack, remove it from the stack
00048 
00049         public bool acceptDifferentItems = false;
00050 
00051         private GameObject spawnedItem;
00052         private bool itemIsSpawned = false;
00053 
00054         public UnityEvent pickupEvent;
00055         public UnityEvent dropEvent;
00056 
00057         public bool justPickedUpItem = false;
00058 
00059 
00060         //-------------------------------------------------
00061         private void CreatePreviewObject()
00062         {
00063             if ( !useItemPackagePreview )
00064             {
00065                 return;
00066             }
00067 
00068             ClearPreview();
00069 
00070             if ( useItemPackagePreview )
00071             {
00072                 if ( itemPackage == null )
00073                 {
00074                     return;
00075                 }
00076 
00077                 if ( useFadedPreview == false ) // if we don't have a spawned item out there, use the regular preview
00078                 {
00079                     if ( itemPackage.previewPrefab != null )
00080                     {
00081                         previewObject = Instantiate( itemPackage.previewPrefab, transform.position, Quaternion.identity ) as GameObject;
00082                         previewObject.transform.parent = transform;
00083                         previewObject.transform.localRotation = Quaternion.identity;
00084                     }
00085                 }
00086                 else // there's a spawned item out there. Use the faded preview
00087                 {
00088                     if ( itemPackage.fadedPreviewPrefab != null )
00089                     {
00090                         previewObject = Instantiate( itemPackage.fadedPreviewPrefab, transform.position, Quaternion.identity ) as GameObject;
00091                         previewObject.transform.parent = transform;
00092                         previewObject.transform.localRotation = Quaternion.identity;
00093                     }
00094                 }
00095             }
00096         }
00097 
00098 
00099         //-------------------------------------------------
00100         void Start()
00101         {
00102             VerifyItemPackage();
00103         }
00104 
00105 
00106         //-------------------------------------------------
00107         private void VerifyItemPackage()
00108         {
00109             if ( itemPackage == null )
00110             {
00111                 ItemPackageNotValid();
00112             }
00113 
00114             if ( itemPackage.itemPrefab == null )
00115             {
00116                 ItemPackageNotValid();
00117             }
00118         }
00119 
00120 
00121         //-------------------------------------------------
00122         private void ItemPackageNotValid()
00123         {
00124             Debug.LogError("<b>[SteamVR Interaction]</b> ItemPackage assigned to " + gameObject.name + " is not valid. Destroying this game object." );
00125             Destroy( gameObject );
00126         }
00127 
00128 
00129         //-------------------------------------------------
00130         private void ClearPreview()
00131         {
00132             foreach ( Transform child in transform )
00133             {
00134                 if ( Time.time > 0 )
00135                 {
00136                     GameObject.Destroy( child.gameObject );
00137                 }
00138                 else
00139                 {
00140                     GameObject.DestroyImmediate( child.gameObject );
00141                 }
00142             }
00143         }
00144 
00145 
00146         //-------------------------------------------------
00147         void Update()
00148         {
00149             if ( ( itemIsSpawned == true ) && ( spawnedItem == null ) )
00150             {
00151                 itemIsSpawned = false;
00152                 useFadedPreview = false;
00153                 dropEvent.Invoke();
00154                 CreatePreviewObject();
00155             }
00156         }
00157 
00158 
00159         //-------------------------------------------------
00160         private void OnHandHoverBegin( Hand hand )
00161         {
00162             ItemPackage currentAttachedItemPackage = GetAttachedItemPackage( hand );
00163 
00164             if ( currentAttachedItemPackage == itemPackage ) // the item at the top of the hand's stack has an associated ItemPackage
00165             {
00166                 if ( takeBackItem && !requireReleaseActionToReturn ) // if we want to take back matching items and aren't waiting for a trigger press
00167                 {
00168                     TakeBackItem( hand );
00169                 }
00170             }
00171 
00172             if (!requireGrabActionToTake) // we don't require trigger press for pickup. Spawn and attach object.
00173             {
00174                 SpawnAndAttachObject( hand, GrabTypes.Scripted );
00175             }
00176 
00177             if (requireGrabActionToTake && showTriggerHint )
00178             {
00179                 hand.ShowGrabHint("PickUp");
00180             }
00181         }
00182 
00183 
00184         //-------------------------------------------------
00185         private void TakeBackItem( Hand hand )
00186         {
00187             RemoveMatchingItemsFromHandStack( itemPackage, hand );
00188 
00189             if ( itemPackage.packageType == ItemPackage.ItemPackageType.TwoHanded )
00190             {
00191                 RemoveMatchingItemsFromHandStack( itemPackage, hand.otherHand );
00192             }
00193         }
00194 
00195 
00196         //-------------------------------------------------
00197         private ItemPackage GetAttachedItemPackage( Hand hand )
00198         {
00199             GameObject currentAttachedObject = hand.currentAttachedObject;
00200 
00201             if ( currentAttachedObject == null ) // verify the hand is holding something
00202             {
00203                 return null;
00204             }
00205 
00206             ItemPackageReference packageReference = hand.currentAttachedObject.GetComponent<ItemPackageReference>();
00207             if ( packageReference == null ) // verify the item in the hand is matchable
00208             {
00209                 return null;
00210             }
00211 
00212             ItemPackage attachedItemPackage = packageReference.itemPackage; // return the ItemPackage reference we find.
00213 
00214             return attachedItemPackage;
00215         }
00216 
00217 
00218         //-------------------------------------------------
00219         private void HandHoverUpdate( Hand hand )
00220         {
00221             if ( takeBackItem && requireReleaseActionToReturn )
00222             {
00223                 if (hand.isActive)
00224                 {
00225                     ItemPackage currentAttachedItemPackage = GetAttachedItemPackage( hand );
00226                     if (currentAttachedItemPackage == itemPackage && hand.IsGrabEnding(currentAttachedItemPackage.gameObject))
00227                     {
00228                         TakeBackItem( hand );
00229                         return; // So that we don't pick up an ItemPackage the same frame that we return it
00230                     }
00231                 }
00232             }
00233 
00234             if ( requireGrabActionToTake )
00235             {
00236                 GrabTypes startingGrab = hand.GetGrabStarting();
00237 
00238                 if (startingGrab != GrabTypes.None)
00239                 {
00240                     SpawnAndAttachObject( hand, GrabTypes.Scripted);
00241                 }
00242             }
00243         }
00244 
00245 
00246         //-------------------------------------------------
00247         private void OnHandHoverEnd( Hand hand )
00248         {
00249             if ( !justPickedUpItem && requireGrabActionToTake && showTriggerHint )
00250             {
00251                 hand.HideGrabHint();
00252             }
00253 
00254             justPickedUpItem = false;
00255         }
00256 
00257 
00258         //-------------------------------------------------
00259         private void RemoveMatchingItemsFromHandStack( ItemPackage package, Hand hand )
00260         {
00261             if (hand == null)
00262                 return;
00263 
00264             for ( int i = 0; i < hand.AttachedObjects.Count; i++ )
00265             {
00266                 ItemPackageReference packageReference = hand.AttachedObjects[i].attachedObject.GetComponent<ItemPackageReference>();
00267                 if ( packageReference != null )
00268                 {
00269                     ItemPackage attachedObjectItemPackage = packageReference.itemPackage;
00270                     if ( ( attachedObjectItemPackage != null ) && ( attachedObjectItemPackage == package ) )
00271                     {
00272                         GameObject detachedItem = hand.AttachedObjects[i].attachedObject;
00273                         hand.DetachObject( detachedItem );
00274                     }
00275                 }
00276             }
00277         }
00278 
00279 
00280         //-------------------------------------------------
00281         private void RemoveMatchingItemTypesFromHand( ItemPackage.ItemPackageType packageType, Hand hand )
00282         {
00283             for ( int i = 0; i < hand.AttachedObjects.Count; i++ )
00284             {
00285                 ItemPackageReference packageReference = hand.AttachedObjects[i].attachedObject.GetComponent<ItemPackageReference>();
00286                 if ( packageReference != null )
00287                 {
00288                     if ( packageReference.itemPackage.packageType == packageType )
00289                     {
00290                         GameObject detachedItem = hand.AttachedObjects[i].attachedObject;
00291                         hand.DetachObject( detachedItem );
00292                     }
00293                 }
00294             }
00295         }
00296 
00297 
00298         //-------------------------------------------------
00299         private void SpawnAndAttachObject( Hand hand, GrabTypes grabType )
00300         {
00301             if ( hand.otherHand != null )
00302             {
00303                 //If the other hand has this item package, take it back from the other hand
00304                 ItemPackage otherHandItemPackage = GetAttachedItemPackage( hand.otherHand );
00305                 if ( otherHandItemPackage == itemPackage )
00306                 {
00307                     TakeBackItem( hand.otherHand );
00308                 }
00309             }
00310 
00311             if ( showTriggerHint )
00312             {
00313                 hand.HideGrabHint();
00314             }
00315 
00316             if ( itemPackage.otherHandItemPrefab != null )
00317             {
00318                 if ( hand.otherHand.hoverLocked )
00319                 {
00320                     Debug.Log( "<b>[SteamVR Interaction]</b> Not attaching objects because other hand is hoverlocked and we can't deliver both items." );
00321                     return;
00322                 }
00323             }
00324 
00325             // if we're trying to spawn a one-handed item, remove one and two-handed items from this hand and two-handed items from both hands
00326             if ( itemPackage.packageType == ItemPackage.ItemPackageType.OneHanded )
00327             {
00328                 RemoveMatchingItemTypesFromHand( ItemPackage.ItemPackageType.OneHanded, hand );
00329                 RemoveMatchingItemTypesFromHand( ItemPackage.ItemPackageType.TwoHanded, hand );
00330                 RemoveMatchingItemTypesFromHand( ItemPackage.ItemPackageType.TwoHanded, hand.otherHand );
00331             }
00332 
00333             // if we're trying to spawn a two-handed item, remove one and two-handed items from both hands
00334             if ( itemPackage.packageType == ItemPackage.ItemPackageType.TwoHanded )
00335             {
00336                 RemoveMatchingItemTypesFromHand( ItemPackage.ItemPackageType.OneHanded, hand );
00337                 RemoveMatchingItemTypesFromHand( ItemPackage.ItemPackageType.OneHanded, hand.otherHand );
00338                 RemoveMatchingItemTypesFromHand( ItemPackage.ItemPackageType.TwoHanded, hand );
00339                 RemoveMatchingItemTypesFromHand( ItemPackage.ItemPackageType.TwoHanded, hand.otherHand );
00340             }
00341 
00342             spawnedItem = GameObject.Instantiate( itemPackage.itemPrefab );
00343             spawnedItem.SetActive( true );
00344             hand.AttachObject( spawnedItem, grabType, attachmentFlags );
00345 
00346             if ( ( itemPackage.otherHandItemPrefab != null ) && ( hand.otherHand.isActive ) )
00347             {
00348                 GameObject otherHandObjectToAttach = GameObject.Instantiate( itemPackage.otherHandItemPrefab );
00349                 otherHandObjectToAttach.SetActive( true );
00350                 hand.otherHand.AttachObject( otherHandObjectToAttach, grabType, attachmentFlags );
00351             }
00352 
00353             itemIsSpawned = true;
00354 
00355             justPickedUpItem = true;
00356 
00357             if ( takeBackItem )
00358             {
00359                 useFadedPreview = true;
00360                 pickupEvent.Invoke();
00361                 CreatePreviewObject();
00362             }
00363         }
00364     }
00365 }
