00876       LEAP_VARIANT_VALUE_TYPE valueStruct = new LEAP_VARIANT_VALUE_TYPE(); //This is a C# approximation of a C union
00877       valueStruct.type = eLeapValueType.eLeapValueType_Boolean;
00878       valueStruct.boolValue = value ? 1 : 0;
00879       return SaveConfigWithValueType(hConnection, key, valueStruct, out requestId);
00880     }
