00893     public static eLeapRS SaveConfigValue(IntPtr hConnection, string key, string value, out UInt32 requestId) {
00894       LEAP_VARIANT_REF_TYPE valueStruct;
00895       valueStruct.type = eLeapValueType.eLeapValueType_String;
00896       valueStruct.stringValue = value;
00897       return SaveConfigWithRefType(hConnection, key, valueStruct, out requestId);
00898     }
00899     private static eLeapRS SaveConfigWithValueType(IntPtr hConnection, string key, LEAP_VARIANT_VALUE_TYPE valueStruct, out UInt32 requestId) {
00900       IntPtr configValue = Marshal.AllocHGlobal(Marshal.SizeOf(valueStruct));
00901       eLeapRS callResult = eLeapRS.eLeapRS_UnknownError;
00902       try {
00903         Marshal.StructureToPtr(valueStruct, configValue, false);
00904         callResult = SaveConfigValue(hConnection, key, configValue, out requestId);
00905       } finally {
00906         Marshal.FreeHGlobal(configValue);
00907       }
00908       return callResult;
00909     }
00910     private static eLeapRS SaveConfigWithRefType(IntPtr hConnection, string key, LEAP_VARIANT_REF_TYPE valueStruct, out UInt32 requestId) {
00911       IntPtr configValue = Marshal.AllocHGlobal(Marshal.SizeOf(valueStruct));
00912       eLeapRS callResult = eLeapRS.eLeapRS_UnknownError;
00913       try {
00914         Marshal.StructureToPtr(valueStruct, configValue, false);
00915         callResult = SaveConfigValue(hConnection, key, configValue, out requestId);
00916       } finally {
00917         Marshal.FreeHGlobal(configValue);
00918       }
00919       return callResult;
00920     }
