00010 using UnityEngine;
00011 using UnityEditor;
00012 
00013 namespace Leap.Unity{
00014   [CustomEditor(typeof(LeapImageRetriever))]
00015   public class LeapImageRetrieverEditor : CustomEditorBase<LeapImageRetriever> {
00016 
00017     private GUIContent _textureGUIContent;
00018     private GUIContent _distortionTextureGUIContent;
00019 
00020     protected override void OnEnable() {
00021       base.OnEnable();
00022 
00023       _textureGUIContent = new GUIContent("Sensor Texture");
00024       _distortionTextureGUIContent = new GUIContent("Distortion Texture");
00025     }
00026 
00027     public override void OnInspectorGUI() {
00028       base.OnInspectorGUI();
00029 
00030       if (Application.isPlaying) {
00031         var data = target.TextureData;
00032         var dataType = typeof(Object);
00033 
00034         EditorGUI.BeginDisabledGroup(true);
00035         EditorGUILayout.ObjectField(_textureGUIContent, data.TextureData.CombinedTexture, dataType, true);
00036         EditorGUILayout.ObjectField(_distortionTextureGUIContent, data.Distortion.CombinedTexture, dataType, true);
00037         EditorGUI.EndDisabledGroup();
00038       }
00039     }
00040   }
00041 }
