00010 using UnityEditor;
00011 using UnityEngine;
00012 using Leap.Unity;
00013 
00014 namespace Leap.Unity {
00015 
00016   [InitializeOnLoad]
00017   public class LeapUnityWindow : EditorWindow {
00018 
00019     #region Settings & Init
00020 
00021     private const string WINDOW_TITLE = "Leap Motion Unity Modules";
00022     private static readonly Vector2 WINDOW_MIN_SIZE = new Vector2(600f, 600f);
00023     private static LeapUnityWindow _currentWindow = null;
00024 
00030     private const string LEAP_UNITY_WINDOW_LAUNCHED_PREF = "Leap Unity Window Launched";
00035     static LeapUnityWindow() {
00036       EditorApplication.delayCall += () => {
00037         if (!EditorPrefs.GetBool(LEAP_UNITY_WINDOW_LAUNCHED_PREF)) {
00038           EditorPrefs.SetBool(LEAP_UNITY_WINDOW_LAUNCHED_PREF, true);
00039           Init();
00040         }
00041       };
00042     }
00043 
00044     [MenuItem("Window/Leap Motion")]
00045     public static void Init() {
00046       var window = (LeapUnityWindow)GetWindow(typeof(LeapUnityWindow),
00047         utility: true, title: WINDOW_TITLE, focus: true);
00048       window.name = "Leap Motion Unity Modules Window";
00049       window.minSize = WINDOW_MIN_SIZE;
00050       _currentWindow = window;
00051     }
00052 
00053     #endregion
00054 
00055     #region Resources
00056 
00057     private string leapLogoResourceName {
00058       get {
00059         if (EditorGUIUtility.isProSkin) return "LM_Logo_White";
00060         else return "LM_Logo_Black";
00061       }
00062     }
00063 
00064     private Texture2D _backingLeapTex = null;
00065     private Texture2D leapTex {
00066       get {
00067         if (_backingLeapTex == null) {
00068           _backingLeapTex = EditorResources.Load<Texture2D>(leapLogoResourceName);
00069         }
00070         return _backingLeapTex;
00071       }
00072     }
00073 
00074     private static GUISkin s_backingWindowSkin;
00075     public static GUISkin windowSkin {
00076       get {
00077         if (s_backingWindowSkin == null) {
00078           s_backingWindowSkin = EditorGUIUtility.GetBuiltinSkin(EditorSkin.Scene);
00079         }
00080         return s_backingWindowSkin;
00081       }
00082     }
00083 
00084     #endregion
00085 
00086     #region Window State
00087 
00088     public static bool isWindowOpen {
00089       get {
00090         return _currentWindow != null;
00091       }
00092     }
00093 
00094     private int _tabIndex = 0;
00095     private static string[] _tabs = new string[] {
00096       "Project Checks", "Rig Upgrader", "Preferences"
00097     };
00098     public static void ShowTab(int tabIndex) {
00099       if (_currentWindow != null) {
00100         _currentWindow._tabIndex = tabIndex;
00101       }
00102     }
00103     public static int GetTabCount() { return _tabs.Length; }
00104 
00105     private Vector2 _scrollPosition = Vector2.zero;
00106 
00107     #endregion
00108 
00109     #region OnGUI
00110 
00111     private void OnGUI() {
00112       var origSkin = GUI.skin;
00113       try {
00114         GUI.skin = windowSkin;
00115 
00116         drawGUI();
00117       }
00118       finally {
00119         GUI.skin = origSkin;
00120       }
00121     }
00122 
00123     private void drawGUI() {
00124       var boxStyle = windowSkin.box;
00125       GUILayout.BeginVertical();
00126 
00127       // Logo.
00128       var logoStyle = windowSkin.box;
00129       logoStyle.fixedHeight = 150;
00130       logoStyle.stretchWidth = true;
00131       logoStyle.alignment = TextAnchor.MiddleCenter;
00132       logoStyle.margin = new RectOffset(0, 0, top: 20, bottom: 20);
00133       GUILayout.Box(new GUIContent(leapTex), logoStyle, GUILayout.ExpandWidth(true),
00134         GUILayout.MaxHeight(150f));
00135 
00136       // Window tabs.
00137       _tabIndex = GUILayout.Toolbar(_tabIndex, _tabs);
00138       _scrollPosition = GUILayout.BeginScrollView(_scrollPosition,
00139         GUILayout.ExpandWidth(true), GUILayout.ExpandHeight(true));
00140       switch (_tabIndex) {
00141         case 0:
00142           LeapProjectChecks.DrawProjectChecksGUI();
00143           break;
00144         case 1:
00145           LeapRigUpgrader.DrawUpgraderGUI();
00146           break;
00147         case 2:
00148           float prevLabelWidth = EditorGUIUtility.labelWidth;
00149           EditorGUIUtility.labelWidth = 200;
00150           LeapPreferences.DrawPreferencesGUI();
00151           EditorGUIUtility.labelWidth = prevLabelWidth;
00152           break;
00153         default:
00154           _tabIndex = 0;
00155           break;
00156       }
00157       GUILayout.EndScrollView();
00158 
00159       GUILayout.EndVertical();
00160     }
00161 
00162     #endregion
