00039         public static void AddFontAsset(TMP_FontAsset fontAsset)
00040         {
00041             MaterialReferenceManager.instance.AddFontAssetInternal(fontAsset);
00042         }
00043 
00048         private void AddFontAssetInternal(TMP_FontAsset fontAsset)
00049         {
00050             if (m_FontAssetReferenceLookup.ContainsKey(fontAsset.hashCode)) return;
00051 
00052             // Add reference to the font asset.
00053             m_FontAssetReferenceLookup.Add(fontAsset.hashCode, fontAsset);
00054 
00055             // Add reference to the font material.
00056             m_FontMaterialReferenceLookup.Add(fontAsset.materialHashCode, fontAsset.material);
00057         }
