00076         private void AddSpriteAssetInternal(TMP_SpriteAsset spriteAsset)
00077         {
00078             if (m_SpriteAssetReferenceLookup.ContainsKey(spriteAsset.hashCode)) return;
00079 
00080             // Add reference to sprite asset.
00081             m_SpriteAssetReferenceLookup.Add(spriteAsset.hashCode, spriteAsset);
00082 
00083             // Adding reference to the sprite asset material as well
00084             m_FontMaterialReferenceLookup.Add(spriteAsset.hashCode, spriteAsset.material);
00085         }
